import axios from "axios";
import axiosRetry from 'axios-retry';
import { getTimeoutConfig } from "axios/AxiosUtils";
import { getRetryConfig } from "axios/AxiosUtils";
import { createBaseHttpRequestBody } from "axios/AxiosUtils";
import { getUserLogin, isAdmin, isSubgerente } from "common/SessionUtils";
import { calculateComponentHeight } from "common/utils/forms";
import AuditorSelector from "components/auditor/AuditorSelector";
import AuditorSimpleInput from "components/auditor/AuditorSimpleInput";
import { ViewsContext } from 'context/ViewsContext';
import { GroupsContext } from "context/GroupsContext";
import { WarningsUpdateContext } from "context/WarningsUpdateContext";
import PauseButton from 'components/buttons/PauseButton';

import React from "react";

import env from "common/enviroment";
import {
  Nav,
  NavItem,
  NavLink,
  Row,
  TabContent,
  TabPane,
  FormGroup,
  Label,
  Input,
  Button,
  Modal,
  ModalBody,
  ModalHeader,
  Col,
  Spinner
} from 'reactstrap';
import SEVERITY_ENUM from "common/enumerators/SeverityEnum";
import { EDITION, ERROR, OK } from "../ModalConsts";
import NOTIFICATION_STATUS_ENUM from "common/enumerators/NotificationStatus";
import { unlockWarning } from "common/rules/NotificationRules";
import MODAL_TYPE_ENUM from 'common/enumerators/ModalTypeEnum';
import { checkSemComandoFiscal } from "common/rules/NotificationRules";
import { isMonitoramento } from "common/SessionUtils";
import { hasUserLoggedData } from "common/SessionUtils";
import { getUserID } from "common/SessionUtils";
import { isTreated } from "common/enumerators/NotificationStatus";
import { isLocked } from "common/enumerators/LockEnum";
import { semComandoFiscalButtonConfig } from "components/auditor/AuditorSelector";
import { emMonitoramentoButtonConfig } from "components/auditor/AuditorSelector";
import { evaluateNoDesignationStatus } from "common/rules/NotificationRules";
import ConfirmationModal from "../ConfirmationModal ";
import MessageModal from "../MessageModal";
import { conditionalFormatingMethod, getUserValues, hasEmptyValues } from "./utils/format-data";
import Group from "components/group/Group";
import { FISCAL_ACTION_AUDITORS } from "common/model/Notification";
import { DESIGNATION_REPORT } from "common/model/Notification";
import { FISCAL_NOTIFICATION_STATUS } from "common/model/Notification";
import { useModalWithData } from "common/hooks/useModalWithData";
import { Notification } from "common/model/Notification";
import SuspensionModal, { REACTIVATION_MODE, SUSPENSION_MODE } from "../SuspensionModal/SuspensionModal";
import { convertUtcToBrazilianDate } from "common/utils/validate";
import { formatCnpjCpf } from "common/utils/validate";
import { isEmpty } from "common/utils/collections";
import { getNowDateTimeString } from "common/utils/validate";
import { escapeSpecialCharacters } from "common/utils/validate";
import { isSuccessStatusCode } from "common/utils/errorTreatment";
import { getValue } from "common/utils/errorTreatment";
import treatError from "common/utils/errorTreatment";
import { DESIGNATION_FORM_DATA } from "./utils/constants";
import { AWARENESS_AUDITOR } from "common/model/Notification";
import { HOMOLOGATION_AUDITOR } from "common/model/Notification";
import { SUPERVISION_AUDITOR } from "common/model/Notification";
import { USER_LOGIN } from "common/model/User";
import { DESIGNATION_AUDITOR } from "common/model/Notification";
import { DESIGNATION_DATE } from "common/model/Notification";
import { FISCAL_NOTIFICATION_MODIFICATION_DATE } from "common/model/Notification";
import { FISCAL_NOTIFICATION_PARENT_ID } from "common/model/Notification";
import { DHEMI } from "common/model/Dfe";
import { CNPJ_ISSUER } from "common/model/Dfe";
import { CPF_ISSUER } from "common/model/Dfe";
import { CNPJ_RECIPIENT } from "common/model/Dfe";
import { CPF_RECIPIENT } from "common/model/Dfe";
import { DFE_VALUE } from "common/model/Dfe";



export default function WarningModal({ show, toggle, data, formData, setFormData, reloadMethod, editionMode = true, spinner: warningModalSpinner = false, tableView = true }) {

  const { getGroupOwnerData, getMonitoringGroupOwner, getGroupSupervisorData } = React.useContext(GroupsContext);
  const { lastWarningUpdate, warningsViewed, setWarningsViewed } = React.useContext(WarningsUpdateContext)
  const { refresh } = React.useContext(ViewsContext)

  const [tabIndex, setTabIndex] = React.useState(1)
  const [invalid, setInvalid] = React.useState(false);
  const [isDisableFromBlock, setIsDisableFromBlock] = React.useState(false)
  const [spinner, setSpinner] = React.useState(false)
  const [pauseState, setPauseState] = React.useState(false)
  const togglePauseState = () => setPauseState(prev => !prev);

  const axiosRetryProxy = axios.create(getTimeoutConfig())
  axiosRetry(axiosRetryProxy, getRetryConfig())

  const { modalData, setModalData, modalActionMethods, setModalActionMethods, isModalActive, modalType, toggleModalOn, toggleModalOff } = useModalWithData()

  let notificationObj = data
  let warningConfigObj = (notificationObj instanceof Notification) ? notificationObj.getWarningConfig() : null
  let eventObj = (notificationObj instanceof Notification) ? notificationObj.getEvent() : null

  React.useEffect(() => {

    if (!isEmpty(warningConfigObj)) {
      setPauseState(warningConfigObj.isConfigPaused())
    }

    //UseEffect para prevenir um reload do browser
    const handleBeforeUnload = (event) => {
      event.preventDefault();
      localStorage.setItem("warningToUnlock", JSON.stringify(data))
    };
    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      localStorage.removeItem("warningToUnlock")
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };

// eslint-disable-next-line react-hooks/exhaustive-deps  
  }, [data]);


  React.useEffect(() => {
    if (Object.keys(data).length !== 0 & tableView) {
      //TODO REVER ESTE CÓDIGO ABAIXO. Ele está funcionando apenas para a última notificação. Acredito que deveria ser em warningsViewed
      //TODO REMOVER A PROPRIEDADE CHECKED. Acho que não faz sentido

      if (isEmpty(lastWarningUpdate) || isEmpty(lastWarningUpdate.id)){
        return
      }

      let warning_id = data['id'].toString()
      let lastWarningID = lastWarningUpdate.id.toString()

      if (isEmpty(warningsViewed[warning_id])){
        return
      }

      if (Object.keys(lastWarningUpdate).length !== 0 & !data['fiscal_notification_lock']) {
        if (lastWarningID === warning_id &  !warningsViewed[warning_id].checked & lastWarningUpdate.status < NOTIFICATION_STATUS_ENUM.DESIGNADA) {
          if (lastWarningUpdate.user !== getUserLogin() & lastWarningUpdate.user !== null) {

            setModalData({ operation: EDITION, messageType: ERROR, message: `Alerta em tratamento por ${lastWarningUpdate.user}!` });
            toggleModalOn(MODAL_TYPE_ENUM.MESSAGE)

            setIsDisableFromBlock(() => true)

            setWarningsViewed((prevState) => ({
              ...prevState,
              [warning_id]: { ...lastWarningUpdate, checked: true }
            }))
          } else {
            setWarningsViewed((prevState) => ({
              ...prevState,
              [warning_id]: { ...prevState[warning_id], checked: true, lock: 0 }
            }))
          }
        }
      }
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [lastWarningUpdate])

  React.useEffect(() => {
    if (!isEmpty(data) & tableView) {
      if (isLocked(data) & !isTreated(data)) {
        let warning_id = data.id.toString()
        if (data.fiscal_notification_lock_user !== getUserLogin()) {
          setModalData({ operation: EDITION, messageType: ERROR, message: `Alerta em tratamento por ${data.fiscal_notification_lock_user}!` });
          toggleModalOn(MODAL_TYPE_ENUM.MESSAGE)

          setIsDisableFromBlock(() => true)

          setWarningsViewed((prevState) => ({
            ...prevState,
            [warning_id]: {
              user: data.fiscal_notification_lock_user, checked: true, lock: 1,
              status: data.fiscal_notification_status === NOTIFICATION_STATUS_ENUM.NAO_VISUALIZADA ? 1 : data.fiscal_notification_status
            }
          }))
        } else {
          setWarningsViewed((prevState) => ({
            ...prevState,
            [warning_id]: { ...prevState[warning_id], checked: true, lock: 0 }
          }))
        }
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [data])


  const setResponsibleAuditors = (fiscal_action_auditors, httpRequestBody) => {
    //o gerente responsável pelo setor de monitoramento do GOFMT deve ser adicioando como responsável pela ciência em todos alertas
    let monitoringChief = getMonitoringGroupOwner()
    httpRequestBody[AWARENESS_AUDITOR] = monitoringChief[USER_LOGIN]

    //caso NÃO tenha sido possível designar o comando fiscal, o gerente responsável 
    //pelo setor de monitoramento do GOFMT deve ser adicioando TAMBÉM como responsável pela HOMOLOGAÇÃO
    if (checkSemComandoFiscal(fiscal_action_auditors)) {
      httpRequestBody[HOMOLOGATION_AUDITOR] = monitoringChief[USER_LOGIN]
    } else {
      //caso tenha sido possível designar o comando fiscal, o gerente responsável 
      //pela GR do comando fiscal deve ser adicioando como responsável pela HOMOLOGAÇÃO
      let homologationAuditor = getGroupOwnerData(fiscal_action_auditors)
      httpRequestBody[HOMOLOGATION_AUDITOR] = homologationAuditor[USER_LOGIN]

      //caso esta gerência, tenha um supervisor, ele deve ser adicionado como responsável pela homologação
      let supervisorAuditor = getGroupSupervisorData(fiscal_action_auditors)
      if (!isEmpty(supervisorAuditor)) {
        httpRequestBody[SUPERVISION_AUDITOR] = supervisorAuditor[USER_LOGIN]
      }
    }
  }



  const updateWarning = () => {

    let warning_id = notificationObj.getId()
    let fiscal_action_auditors = formData[FISCAL_ACTION_AUDITORS]
    let status = NOTIFICATION_STATUS_ENUM.DESIGNADA
    if (checkSemComandoFiscal(fiscal_action_auditors)) {
      status = evaluateNoDesignationStatus(fiscal_action_auditors)
    }

    let httpRequestBody = createBaseHttpRequestBody({
      [FISCAL_ACTION_AUDITORS]: getUserValues(Object.values(fiscal_action_auditors)),
      [DESIGNATION_AUDITOR]: getUserLogin(),
      [DESIGNATION_DATE]: getNowDateTimeString(),
      [DESIGNATION_REPORT]: escapeSpecialCharacters(formData[DESIGNATION_REPORT]),
      [FISCAL_NOTIFICATION_MODIFICATION_DATE]: getNowDateTimeString(),
      [FISCAL_NOTIFICATION_PARENT_ID]: warning_id,
      [FISCAL_NOTIFICATION_STATUS]: status
    })

    //Se o alerta não foi marcado como em monitoramento, não deve ser adicionado responsáveis pela homologação, ciência ou supervisão. 
    //caso contrário, deve ser adicionado os responsáveis
    if (status !== NOTIFICATION_STATUS_ENUM.EM_MONITORAMENTO) {
      setResponsibleAuditors(fiscal_action_auditors, httpRequestBody)
    }

    setSpinner(true)
    toggleModalOn(MODAL_TYPE_ENUM.MESSAGE)
    axiosRetryProxy.patch(`${env.apiAddress}/warnings/update/${warning_id}`, httpRequestBody)
      .then(res => {
        if (!isSuccessStatusCode(res) || !getValue(res, 'id')) {
          setModalData({ operation: EDITION, messageType: ERROR, message: "Falha no cadastro dos fiscais designados" });
        } else {

          console.log('atualizando status do alerta de id', warning_id)

          //TODO CONVERTER ESTE CÒDIGO ABAIXO EM Método. 
          setWarningsViewed((prevState) => ({
            ...prevState,
            [warning_id]: {
              ...prevState[warning_id], checked: false, lock: 0,
              status: httpRequestBody[FISCAL_NOTIFICATION_STATUS]
            }
          }))

          reloadMethod()
          setModalData({ operation: EDITION, messageType: OK, message: "Cadastro dos fiscais designados realizado com sucesso" });
        }
      }).catch(err => {
        setModalData(treatError(err, "Cadastro dos Fiscais Designados", EDITION));
        console.log(err)
      }).finally(() => {
        setSpinner(false)
        toggleEditionModal(status);
      })

  }





  //TODO remover estes filtros abaixo e usar notification_status. 
  const isEditionModeDisabled = () => {

    if (isDisableFromBlock)
      return true

    if (notificationObj.getLockState() & notificationObj.getDesignationAuditor() === getUserLogin() && notificationObj.getStatus() >= NOTIFICATION_STATUS_ENUM.REALIZADA)
      return false

    if (notificationObj.getLockState() & notificationObj.getDesignationAuditor() !== getUserLogin())
      return true

    if (notificationObj.getFiscalActionAuditors() !== null & notificationObj.getDesignationAuditor() !== getUserLogin())
      return true

    if (!editionMode)
      return true

    if (isAdmin()) {
      return false
    } else {
      return (!hasUserLoggedData() || !getUserID()) || notificationObj.getHomologationDate()
    }

  }

  // tem dois isDisabled, usar o dessa página ou criar um novo?

  const isPauseButtonDisabled = (criador) => {
    let user = getUserLogin()

    if ((user && criador === user) || isAdmin() || isSubgerente() || isMonitoramento()) {
      return false;
    } else {
      return true;
    }
  }





  const toggleEditionModal = (status) => {
    if (typeof status === 'undefined' || typeof status !== 'number') {
      status = notificationObj.getStatus()
    }
    if (!isDisableFromBlock && !notificationObj.isTreated(status) && tableView) { //não é necessario desbloquear alertas com tratamento já realizado
      // SE O ALERTA JÁ TIVER SIDO DESIGNADO, SOMENTE O USUÁRIO RESPONSÁVEL PELO MONITORAMENTO PODERÁ DESBLOQUEÁ-LO
      if (status <= NOTIFICATION_STATUS_ENUM.VISUALIZADA) {
        unlockWarning(notificationObj.getRawData(), status, warningsViewed, setWarningsViewed)
      }
    }
    toggle()
  }

  const openReactivationModal = (warningConfigObj) => {
    setInvalid(false)
    let modalData = { mode: REACTIVATION_MODE, warningConfig: warningConfigObj }
    toggleModalOn(MODAL_TYPE_ENUM.WARNING_PAUSE, modalData)
  }

  const openSuspensionModal = (warningConfigObj) => {
    setInvalid(false)
    let modalData = { mode: SUSPENSION_MODE, warningConfig: warningConfigObj }
    toggleModalOn(MODAL_TYPE_ENUM.WARNING_PAUSE, modalData)
  }


  const openConfirmationModal = () => {
    if (hasEmptyValues(formData)) {
      setInvalid(true)
      let modalData = { operation: EDITION, messageType: ERROR, message: "Há campos obrigatórios com preenchimento pendente!" };
      toggleModalOn(MODAL_TYPE_ENUM.MESSAGE, modalData)
    } else {
      setInvalid(false)
      toggleModalOn(MODAL_TYPE_ENUM.CONFIRMATION)
      setModalActionMethods([undefined, updateWarning, undefined]);
      setModalData({
        entity: "a Notificação",
        operation: EDITION
      })

    }
  }

  const cleanInput = () => {
    setInvalid(false);
    setFormData({ ...DESIGNATION_FORM_DATA, [FISCAL_ACTION_AUDITORS]: {} })
  }



  return <>

    <Modal isOpen={show} toggle={toggleEditionModal} size={"lg"} >
      <ModalHeader toggle={toggleEditionModal}>ALERTA:{warningConfigObj.getName()}</ModalHeader>
      <ModalBody style={{ padding: 0 }}>
        {warningModalSpinner || isEmpty(data) ?
          <Spinner color="info"></Spinner>
          :
          <>
            <Nav tabs style={{ background: "#f2f2f2" }}>
              <NavItem style={{ cursor: "pointer" }}>
                <NavLink
                  className={tabIndex === 1 && "active"}
                  onClick={() => { setTabIndex(1) }}
                >
                  Informações
                </NavLink>
              </NavItem>
              <NavItem style={{ cursor: "pointer" }}>
                <NavLink
                  className={tabIndex === 2 && "active"}
                  onClick={() => { setTabIndex(2) }}
                >
                  Ação Fiscal
                </NavLink>
              </NavItem>
              <NavItem style={{ cursor: "pointer" }}>
                <NavLink
                  className={tabIndex === 3 && "active"}
                  onClick={() => { setTabIndex(3) }}
                >
                  Responsáveis
                </NavLink>
              </NavItem>
            </Nav>

            <TabContent activeTab={`${tabIndex}`}>

              <TabPane tabId="1">
                <FormGroup row style={{ margin: 15 }}>
                  <Label style={{ marginRight: '3px' }}>
                    <b>Prioridade:</b>
                    {` ${SEVERITY_ENUM[warningConfigObj.getSeverity()]}`}
                  </Label>
                </FormGroup>
                {tableView && <><FormGroup row style={{ margin: 15 }}>
                  <Label style={{ marginRight: '3px' }}>
                    <b>Local:</b>
                    {` ${eventObj.getLocal()}`}
                  </Label>
                </FormGroup>

                  <FormGroup row style={{ margin: 15 }}>
                    <Label style={{ marginRight: '3px' }}>
                      <b>Data/Hora Passagem:</b>
                      {` ${convertUtcToBrazilianDate(eventObj.getCreationTimestamp())}`}
                    </Label>
                  </FormGroup>


                  <FormGroup row style={{ margin: 15 }}>
                    <Label style={{ marginRight: '3px' }}>
                      <b>Data/Hora Recebimento:</b>
                      {` ${convertUtcToBrazilianDate(eventObj.getReceptionTimestamp())}`}
                    </Label>
                  </FormGroup>


                  <FormGroup row style={{ margin: 15 }}>
                    <Label style={{ marginRight: '3px' }}>
                      <b>Veículo:</b>
                      {` ${eventObj.getVehiclePlate()}`}
                    </Label>
                  </FormGroup>
                </>

                }

                {!tableView && <>
                  <FormGroup row style={{ margin: 15 }}>
                    <Label style={{ marginRight: '3px' }}>
                      <b>Data/Hora Emissão:</b>
                      {` ${convertUtcToBrazilianDate(notificationObj.getFromRawData(DHEMI))}`}
                    </Label>
                  </FormGroup>


                  <FormGroup row style={{ margin: 15 }}>
                    <Label style={{ marginRight: '3px' }}>
                      <b>CNPJ/CPF Emitente:</b>
                      {` ${notificationObj.getFromRawData(CNPJ_ISSUER) ? formatCnpjCpf(notificationObj.getFromRawData(CNPJ_ISSUER)) : formatCnpjCpf(notificationObj.getFromRawData(CPF_ISSUER))}`}
                    </Label>
                  </FormGroup>

                  <FormGroup row style={{ margin: 15 }}>
                    <Label style={{ marginRight: '3px' }}>
                      <b>CNPJ/CPF Destinatário:</b>
                      {` ${notificationObj.getFromRawData(CNPJ_RECIPIENT) ? formatCnpjCpf(notificationObj.getFromRawData(CNPJ_RECIPIENT)) : formatCnpjCpf(notificationObj.getFromRawData(CPF_RECIPIENT))}`}
                    </Label>
                  </FormGroup>


                  <FormGroup row style={{ margin: 15 }}>
                    <Label style={{ marginRight: '3px' }}>
                      <b>Valor:</b>
                      {` R$ ${notificationObj.getFromRawData(DFE_VALUE).toLocaleString('pt-br', { minimumFractionDigits: 2 })}` }
                    </Label>
                  </FormGroup>
                </>



                }

                <FormGroup row style={{ margin: 15 }}>
                  <Label style={{ marginRight: '3px' }}>
                    <b>Descrição:</b>
                  </Label>
                  <Input
                    type='textarea'
                    readOnly
                    value={warningConfigObj.getDescription()}
                    maxLength="2048"
                    style={calculateComponentHeight(warningConfigObj.getDescription(), "0 14px")}
                  />
                </FormGroup>


                <FormGroup row style={{ margin: 15 }}>
                  <Label style={{ marginRight: '3px' }}>
                    <b>Condições:</b>
                  </Label>
                  <Input
                    type='textarea'
                    readOnly
                    value={warningConfigObj.getMonitoringParametersFormatedText()}
                    maxLength="2048"
                    style={calculateComponentHeight(warningConfigObj.getMonitoringParametersFormatedText(), "0 14px")}
                  />
                </FormGroup>

                <FormGroup row style={{ margin: 15 }}>
                  <Label style={{ marginRight: '3px' }}>
                    <b>Visibilidade:</b>
                  </Label>
                  <Group data={warningConfigObj} />

                </FormGroup>

              </TabPane>

              <TabPane tabId="2">
                <FormGroup row style={{ margin: 15 }}>
                  <Label style={{ marginRight: '3px' }}><b>Ação Fiscal:</b></Label>
                  <Input
                    type='textarea'
                    readOnly
                    rows="6"
                    value={warningConfigObj.getFiscalAction()}
                    maxLength="2048"
                    style={{ maxHeight: 240 }}
                  />
                </FormGroup>
              </TabPane>

              <TabPane tabId="3">
                <FormGroup row style={{ margin: 15 }}>
                  <AuditorSimpleInput
                    label={"Responsável pelo Monitoramento:"}
                    user_name={formData.monitoring_user_name}
                    user_registration_id={formData.monitoring_user_registration_id}
                    user_login={formData.monitoring_user_login}
                    invalidFlag={invalid}
                    conditionalFormatingMethod={conditionalFormatingMethod}
                    modalReadonlyMode={isEditionModeDisabled()}
                  />
                </FormGroup>


                <FormGroup row style={{ position: "relative", right: "15px", margin: 15 }}>
                  <AuditorSelector
                    label={"Responsáveis pela Ação Fiscal:"}
                    singleSelectionMode={false}
                    data={formData}
                    setData={setFormData}
                    fieldName={FISCAL_ACTION_AUDITORS}
                    invalidFlag={invalid}
                    modalReadonlyMode={isEditionModeDisabled()}
                    firstOptionalButtonConfig={semComandoFiscalButtonConfig}
                    secondOptionalButtonConfig={emMonitoramentoButtonConfig}
                  />
                </FormGroup>

                <FormGroup row style={{ margin: 15 }}>

                  <Label style={{ marginRight: '3px' }}><b>Relatório Monitoramento:</b></Label>

                  <Input
                    type='textarea'
                    rows="6"
                    maxLength="2048"
                    disabled={isEditionModeDisabled()}
                    style={{ maxHeight: 240 }}
                    value={formData[DESIGNATION_REPORT]}
                    invalid={invalid && isEmpty(formData[DESIGNATION_REPORT])}
                    onChange={e => setFormData({ ...formData, [DESIGNATION_REPORT]: e.target.value })}

                  />

                </FormGroup>
              </TabPane>


            </TabContent>
            <hr />
            <Row>
              <Col style={{ display: "flex", justifyContent: "flex-end", margin: "0 2rem 1rem 0" }}>

                <PauseButton
                  modal={true} data={warningConfigObj}
                  pauseState={pauseState} isDisabled={isPauseButtonDisabled}
                  pauseAction={openSuspensionModal} resumeAction={openReactivationModal}

                />

                <Button
                  color='primary'
                  disabled={isEditionModeDisabled()}
                  onClick={() => { openConfirmationModal(); }}
                  style={{ marginRight: "1rem" }}
                >
                  Salvar
                </Button>

                <Button
                  color='danger'
                  disabled={isEditionModeDisabled()}
                  onClick={() => { cleanInput(); toggleEditionModal(); }}
                >
                  Cancelar
                </Button>

              </Col>
            </Row>
          </>
        }

      </ModalBody>
    </Modal>;


    {modalType === MODAL_TYPE_ENUM.CONFIRMATION && <ConfirmationModal show={isModalActive} toggle={toggleModalOff} data={modalData} actionMethods={modalActionMethods} />}
    {modalType === MODAL_TYPE_ENUM.WARNING_PAUSE && <SuspensionModal show={isModalActive} toggle={toggleModalOff} data={modalData} setData={setModalData} refresh={refresh} togglePauseState={togglePauseState} />}
    {modalType === MODAL_TYPE_ENUM.MESSAGE && <MessageModal show={isModalActive} toggle={toggleModalOff} data={modalData} spinner={spinner} />}

  </>
}
