import React from "react";
import Pagination from "components/tables/Pagination.js";
import toDMS from "common/toDMS.js"

export default function SelectableTable(props) {
  const [pageIndex, setPageIndex] = React.useState(0);
  const [pointsData, pointClick] = props.state;

  return (
    <div className="tableContainer">
      <table responsive={"true"} cellSpacing={0}>
        <thead className="text-primary">
          <tr>
            <th key='name'>Nome</th>
            <th key='description'>Descrição</th>
            <th key='citie'>Município</th>
            <th key='latlng'>Coordenadas</th>
            <th key='category'>Categoria</th>
            <th key='status'>Status</th>
          </tr>
        </thead>
        <tbody>
          {
            props.data
              .slice(
                pageIndex * props.pageSize,
                pageIndex * props.pageSize + props.pageSize
              )
              .map((row, index) => {
                const i = pageIndex * props.pageSize + index
                const coordenadas = toDMS(row['nrlatitude'], row['nrlongitude'])
                return (
                  <tr key={row['id']} className={pointsData[i]? "deleteContainer" : ""} onClick={pointClick(i)}>
                    <td>{row['name']}</td>
                    <td>{row['description']}</td>
                    <td>{row['municipio']}</td>
                    {/* <td>{toDMS(row['nrlatitude'], row['nrlongitude'])} ({row['nrlatitude'].toFixed(6)}, {row['nrlongitude'].toFixed(6)})</td> */}
                    <td>{coordenadas[0]}<br/>{coordenadas[1]}</td>
                    <td>{row['category']}</td>
                    <td>{row['status']?'Ativado' : 'Desativado'}</td>
                  </tr>
                )
              })
          }
        </tbody>
      </table>
      <Pagination length={props.data.length} pageSize={props.pageSize} state={[pageIndex, setPageIndex]}/>
    </div>
  );
}
