import { isPRF } from 'common/SessionUtils'
import { isMonitoramento } from 'common/SessionUtils'
import { isDNIT } from 'common/SessionUtils'
import { isAdmin, isSubgerente, isGaeco, isComandoFiscal} from 'common/SessionUtils'

export function mountViews(routes, history) {

  if (isAdmin()) {

    let ADICIONAR = ['Gerenciar Câmeras', 'Mapa', 'Configurar Alertas', 'Consultar CNPJ', 'Notificações', 'Gerenciar Auditores', 'Gerenciar Grupos de Visibilidade', 'Consultar Veículo', 'Tabela de Emissões de DFe', 'Cadastro de Rádios', 'Status']

    for (let index = 0; index < routes.length; index++) {
      if (ADICIONAR.includes(routes[index]['name'])) {
        routes[index]['visible'] = true
      }
    }
  }
  else if (isSubgerente() || isMonitoramento()) {

    let EXCLUIR = ['Gerenciar Câmeras', 'Gerenciar Auditores', 'Gerenciar Grupos de Visibilidade', 'Cadastro de Rádios']
    let ADICIONAR = ['Configurar Alertas', 'Tabela de Alertas e Tráfego', 'Notificações', 'Consultar Veículo', 'Mapa', 'Tabela de Emissões de DFe', 'Consultar CNPJ', 'Status']

    for (let index = 0; index < routes.length; index++) {
      if (ADICIONAR.includes(routes[index]['name'])) {
        routes[index]['visible'] = true
      }

      if (EXCLUIR.includes(routes[index]['name'])) {
        routes[index]['visible'] = false
      }
    }
  }

  else if (isGaeco() || isDNIT() || isPRF()) {
    let EXCLUIR = ['Gerenciar Câmeras', 'Mapa', 'Consultar CNPJ', 'Gerenciar Auditores', 'Gerenciar Grupos de Visibilidade', 'Consultar Veículo', 'Tabela de Emissões de DFe', 'Status', 'Cadastro de Rádios'];
    let ADICIONAR = ['Configurar Alertas', 'Tabela de Alertas e Tráfego']

    for (let index = 0; index < routes.length; index++) {

      if (ADICIONAR.includes(routes[index]['name'])) {
        routes[index]['visible'] = true
      }

      if (EXCLUIR.includes(routes[index]['name'])) {
        routes[index]['visible'] = false;
      }
    }
  }

  else if (isComandoFiscal()) {
    let EXCLUIR = ['Tabela de Alertas e Tráfego', 'Gerenciar Câmeras', 'Mapa', 'Configurar Alertas', 'Consultar CNPJ', 'Gerenciar Auditores', 'Gerenciar Grupos de Visibilidade', 'Consultar Veículo', 'Tabela de Emissões de DFe', 'Status', 'Cadastro de Rádios'];
    let ADICIONAR = ['Notificações']

    for (let index = 0; index < routes.length; index++) {
      if (ADICIONAR.includes(routes[index]['name'])) {
        routes[index]['visible'] = true
      }


      if (EXCLUIR.includes(routes[index]['name'])) {
        routes[index]['visible'] = false;
      }
    }
  }

  else {
    let EXCLUIR = ['Gerenciar Câmeras', 'Mapa', 'Configurar Alertas', 'Consultar CNPJ', 'Notificações', 'Gerenciar Auditores', 'Gerenciar Grupos de Visibilidade', 'Consultar Veículo', 'Tabela de Emissões de DFe', 'Status', 'Cadastro de Rádios']

    for (let index = 0; index < routes.length; index++) {
      if (EXCLUIR.includes(routes[index]['name'])) {
        routes[index]['visible'] = false
      }
    }
  }
}
