import { FISCAL_ACTION_DATE } from "common/model/Notification";
import { FISCAL_ACTION_REPORT } from "common/model/Notification";
import { HISTORY } from "common/model/Notification";
import { HOMOLOGATION_AUDITOR } from "common/model/Notification";
import { MONITORING_USER_LOGIN } from "common/model/Notification";
import { REPORT_ENABLED } from "common/model/Notification";
import { MONITORING_USER_REGISTRATION_ID } from "common/model/Notification";
import { MONITORING_USER_ID } from "common/model/Notification";
import { MONITORING_USER_NAME } from "common/model/Notification";
import { FISCAL_ACTION_REPORT_DATE } from "common/model/Notification";
import { FISCAL_ACTION_AUDITORS } from "common/model/Notification";
import { BsFileX } from "react-icons/bs";
import { Col, Row } from "reactstrap";

export const emptyBackground = (
  <Row style={{ textAlign: 'center' }}>
    <Col style={{ textAlign: 'center' }} xl={12} lg={12} md={12} sm={8}><BsFileX style={{ fontSize: '140px', marginTop: '60px', marginBottom: '38px', color: "#828282" }} /></Col>
    <Col style={{ textAlign: 'center', fontSize: '20px' }} xl={12} lg={12} md={12} sm={8}><span style={{ color: '#7F8285' }}>Você ainda não possui imagens carregadas</span></Col>
  </Row>
)



export const DATA_ONLY = 0
export const IMAGE_ONLY = 1
export const MERGE = 2
export const NOTHING = 3


export const DEFAULT_TAB_PANE_NAMES = { 'Alerta': 1, 'Monitoramento': 2, 'Ação Fiscal Realizada': 3, 'Dados da Autuação': 4, 'Imagens': 5, 'Homologação': 7, 'Histórico': 8 }
export const TAB_PANE_NAME_WITH_SUPERVISION = { 'Alerta': 1, 'Monitoramento': 2, 'Ação Fiscal Realizada': 3, 'Dados da Autuação': 4, 'Imagens': 5, 'Supervisão': 6, 'Homologação': 7, 'Histórico': 8 }

export const MODE_PANE_MAP = { 
  'edition': [3, 4, 5], 
  'awareness': [2],
  'homologation': [7],
  'supervision': [6],
}

export const BLANK_NOTIFICATION_FORM_DATA = {
  [FISCAL_ACTION_AUDITORS]: {},
  [FISCAL_ACTION_DATE]: "",
  [FISCAL_ACTION_REPORT]: "",
  [FISCAL_ACTION_REPORT_DATE]: "",
  [HISTORY]: "",
  [HOMOLOGATION_AUDITOR]: "",
  [MONITORING_USER_NAME]: '',
  [MONITORING_USER_ID]: '',
  [MONITORING_USER_LOGIN]: '',
  [MONITORING_USER_REGISTRATION_ID]: '',
  [REPORT_ENABLED]: false
}
