/*!

=========================================================
* Paper Dashboard React - v1.2.0
=========================================================

* Product Page: https://www.creative-tim.com/product/paper-dashboard-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)

* Licensed under MIT (https://github.com/creativetimofficial/paper-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { useMemo } from "react";
import {
  Card,
  CardBody,
  Row,
  Col,
} from "reactstrap";

import env from "common/enviroment";

import { useHistory } from "react-router-dom";
import { useJsApiLoader } from '@react-google-maps/api';
import { NavigationContext } from 'context/NavigationContext'
import { WarningsContext } from 'context/WarningsContext';
import { GroupsContext } from 'context/GroupsContext';
import { AuditorsContext } from 'context/AuditorsContext';
import { WarningsUpdateContext } from "context/WarningsUpdateContext";
import Tabs from "components/Tabs";
import MODAL_TYPE_ENUM from "common/enumerators/ModalTypeEnum";
import { isSubgerente, isNearClosingWarningModalEnabled, setNearClosingWarningModalEnabled } from "common/SessionUtils";
import { isAdmin, isLoggedIn, reloadVisibilityGroups } from "common/SessionUtils";
import { isMonitoramento } from "common/SessionUtils";
import { isExternalUser } from "common/SessionUtils";
import { useModalWithData } from "common/hooks/useModalWithData";
import useToggle from "common/hooks/useToogle";
import { isEmpty } from "common/utils/collections";
import ReadNearClosingWarningConfig from "axios/ReadNearClosingWarningConfig";
import NearClosingWarningModal from "components/modals/NearClosingWarningModal/NearClosingWarningModal";
import WarningTable from "./tables/WarningTable";
import TrafficNoDfeTable from "./tables/TrafficNoDfeTable";
import TrafficDfeTable from "./tables/TrafficDfeTable";
import LocationTrackingModal from "components/modals/LocationTrackingModal/LocationTrackingModal";
import useAsync from "common/hooks/useAsync";
import LoadingPage from "layouts/Loading";
import { delay } from "common/utils/forms";


export default function Traffic(props) {
  const history = useHistory()
  const { fetchGroupsData, groupsDataMap, groupsSimpleMap, fetchGroupsName  } = React.useContext(GroupsContext);
  const { setIsMap } = React.useContext(NavigationContext);
  const { startSSE, wasReloaded, setWasReloaded } = React.useContext(WarningsContext);
  const { startWarningsViewedSSE } = React.useContext(WarningsUpdateContext)
  const { auditorsData, getAuditorsData } = React.useContext(AuditorsContext);
  const [tabIndex, setTabIndex] = React.useState(0)
  const [fetchDataFlag, toggleFetchData] = useToggle(false)
  const { modalData, setModalData, isModalActive, modalType, toggleModalOn, toggleModalOff } = useModalWithData()

  const tabs = isAdmin() || isSubgerente() || isMonitoramento() || isExternalUser() ? ['Tabela de alertas', 'Tabela de tr치fego sem DFe', 'Tabela de tr치fego com DFe'] : ['Tabela de alertas']

  const { readNearClosingWarningConfigRequest } = ReadNearClosingWarningConfig()


  const { isLoaded, loadError } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: env.googleApiKey
  })

  const { loading } = useAsync(async () => {
    if (wasReloaded && isLoggedIn()) {
      setIsMap(false);
      reloadVisibilityGroups()

      if (isEmpty(groupsDataMap)) {
        await fetchGroupsData()
      }

      if (isEmpty(groupsSimpleMap)) {
        await fetchGroupsName()
      }

      if (isEmpty(auditorsData)) {
        await getAuditorsData();
      }

      await emitNearCloseWarningNotification()

      startWarningsViewedSSE()
      startSSE()

      await delay(2000);
      setWasReloaded(false)
    }
  }, [wasReloaded]);


  React.useEffect(() => {
    if (!isLoggedIn()) {
      history.push('/login')
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])



  const emitNearCloseWarningNotification = async () => {
    if (!isNearClosingWarningModalEnabled())
      return

    let result = await readNearClosingWarningConfigRequest()
    if (result.length > 0) {
      toggleModalOn(MODAL_TYPE_ENUM.NEAR_CLOSING_WARNING, result)

    }

    setNearClosingWarningModalEnabled(false);
  }


  const toggleMapModal = (data) => {
    setModalData(data)
    toggleModalOn(MODAL_TYPE_ENUM.MAP, data)
    toggleFetchData()
  }


  // eslint-disable-next-line react-hooks/exhaustive-deps
  const createMapModal = useMemo(() => <LocationTrackingModal show={isModalActive} googleMapOnLoad={isLoaded} googleMapOnError={loadError} toggle={toggleModalOff} data={modalData} fetchData={fetchDataFlag} />, [modalData]);

  if (loading && wasReloaded) {
    return <LoadingPage />
  } else {
    return (
      <>

        <div className="content">
          <Row>
            <Col md="12">
              <Tabs names={tabs} index={[tabIndex, setTabIndex]} />
              <Card className="card" style={{ borderTopLeftRadius: '0px' }}>
                <CardBody>
                  {tabIndex === 0 &&
                    <WarningTable toggleMapModal={toggleMapModal} />
                  }
                  {tabIndex === 1 &&
                    <TrafficNoDfeTable toggleMapModal={toggleMapModal} />
                  }
                  {tabIndex === 2 &&
                    <TrafficDfeTable toggleMapModal={toggleMapModal} />

                  }
                </CardBody>
              </Card>
            </Col>
          </Row>

          {modalType === MODAL_TYPE_ENUM.MAP && createMapModal}
          {modalType === MODAL_TYPE_ENUM.NEAR_CLOSING_WARNING && <NearClosingWarningModal show={isModalActive} toggle={toggleModalOff} data={modalData} />}

        </div>
      </>
    );
  }
}
