import React from "react";
import {
	InputGroup,
	Input,
	DropdownToggle,
	DropdownMenu,
	DropdownItem,
	Button,
	UncontrolledButtonDropdown
} from "reactstrap";
import { v4 } from 'uuid'
import { RiDeleteBin2Fill } from "react-icons/ri"

import { isAdmin } from 'common/SessionUtils'
import Tag from "components/tags/Tag"
import { mountQueryFromTags } from "axios/AxiosUtils";

const smScreenSize = 640

export default function TagsInput(props) {
	const [internalTags, setInternalTags] = React.useState({})
	const [isInterval, setIsInterval] = React.useState(false)
	const [input, setInput] = React.useState("")
	const [intervalInput, setIntervalInput] = React.useState("")
	const [columnIndex, setColumn] = React.useState(-1)
	const [errorCode, setErrorCode] = React.useState(-1)
	const [isDesktop, setDesktop] = React.useState(window.innerWidth > smScreenSize)

	const [tags, setTags] = props.tagsState || [internalTags, setInternalTags]
	const columns = props.columns || {}
	const columnsNames = Object.keys(columns).filter(columnName => props.columns[columnName].adminOnly ? isAdmin() : true)
	const setQuery = props.setQuery || (() => { })
	const defaultText = props.defaultText ||  isDesktop? "Selecione" : ""
	const errors = ["Valor j치 inserido!", "Campo vazio! Insira algum valor.", "Formato inv치lido!"]


  const updateMedia = () => { setDesktop(window.innerWidth > smScreenSize) }


	const clearInputs = () => {
		setInput("")
		setIntervalInput("")
		setIsInterval(false)
		setErrorCode(-1)
	}

	const selectColumn = () => columnIndex > -1 ? columnsNames[columnIndex] : defaultText

	const removeTags = (column, indexToRemove) => {
		const updatedTags = { ...tags, [column]: tags[column].filter((_, index) => index !== indexToRemove) }
		setTags(updatedTags)
		setQuery(mountQueryFromTags(updatedTags, columns))
	}

	const validate = (value, currentColumn) => {
		if (!value) {
			setErrorCode(1)
			return false
		}
		if (columns[currentColumn].regex && !columns[currentColumn].regex.test(value)) {
			setErrorCode(2)
			return false
		}
		return true
	}

	const addTags = () => {
		const currentColumn = columnsNames[columnIndex]
		if (!validate(input.trim(), currentColumn) || (isInterval && !validate(intervalInput.trim(), currentColumn)))
			return

		const columnFilters = tags[currentColumn] || []
		const mask = columns[currentColumn].mask ? columns[currentColumn].mask.encode : (v => v)
		const filter = isInterval ? [mask(input.trim()), mask(intervalInput.trim())] : mask(input.trim())
		if (columnFilters.includes(filter)) {
			setErrorCode(0)
			return
		}
		const updatedTags = { ...tags, [currentColumn]: [...columnFilters, filter] }

		clearInputs()
		setColumn(-1)
		setTags(updatedTags)
		setQuery(mountQueryFromTags(updatedTags, columns))
	}

	const validateAndUpdate = (e, filter, setter) => (
		filter.alwaysValidate? filter.regex.test(e.target.value) && setter(e.target.value): setter(e.target.value)
	)

	React.useEffect(() => {
    window.addEventListener("resize", updateMedia);
    return () => window.removeEventListener("resize", updateMedia);
  })

	return (
		<div className={props.size === "lg"? "tags-input-lg":"tags-input"}>
			<div className="input-row">
				<InputGroup className="tags-input-group">
					<UncontrolledButtonDropdown style={{ borderRadius: "4px 0px 0px 4px" }}>
						<DropdownToggle caret color="primary" style={{ borderRadius: "4px 0px 0px 4px" }}>
							{selectColumn()}
						</DropdownToggle>
						<DropdownMenu>
							{columnsNames.map((columnName, index) => {
								return (
									(columnName.includes('BLANK')) ?
										<DropdownItem key={v4()}  divider />
										:
										<DropdownItem key={index} onClick={() => { clearInputs(); setColumn(index) }}>
											{columnName}
										</DropdownItem>
								);
							})}
						</DropdownMenu>
					</UncontrolledButtonDropdown>

					<Input
						value={input}
						type={(columnIndex > -1) ? columns[columnsNames[columnIndex]].inputType : "text"}
						invalid={errorCode > -1}
						disabled={columnIndex === -1}
						onChange={event => validateAndUpdate(event, columns[columnsNames[columnIndex]], setInput)}
						onKeyUp={event => event.key === "Enter" && addTags()}
						placeholder="Digite o filtro"
						style={{ borderRadius: "0px" }}
						step={1}
					>
						{(columnIndex > -1 && columns[columnsNames[columnIndex]].dropdown) ?
							<>
								<option key="0_select" />
								{Object.keys(columns[columnsNames[columnIndex]].dropdown).map((key) => {
									if (columns[columnsNames[columnIndex]].dropdown[key].adminOnly && !isAdmin())
										return ""
									return (
										<option value={key} key={`0_${key}`}>{key}</option>
									)
								})}
							</>
							:
							null
						}
					</Input>

					{(columnIndex > -1 && !isInterval && columns[columnsNames[columnIndex]].supportsInterval) &&
						<Button
							style={{ borderRadius: "0px" }}
							onClick={() => setIsInterval(true)}
						>
							+ Intervalo
						</Button>
					}


					{isInterval &&
						<>
							<Input
								value={intervalInput}
								type={columns[columnsNames[columnIndex]].inputType}
								invalid={errorCode > -1}
								onChange={event => validateAndUpdate(event, columns[columnsNames[columnIndex]], setIntervalInput)}
								onKeyUp={event => event.key === "Enter" && addTags()}
								placeholder="Digite o filtro"
								style={{ borderRadius: "0px" }}
								step={1}
							>
								{columns[columnsNames[columnIndex]].dropdown &&
									<>
										<option key="1_select"/>
										{Object.keys(columns[columnsNames[columnIndex]].dropdown).map((key) => {
											return (
												<option value={key} key={`1_${key}`}>{key}</option>
											)
										})}
									</>
								}
							</Input>
							<Button
								style={{ borderRadius: 0, padding: "0px 12px" }}
								onClick={() => { setIsInterval(false) }}
							>
								<RiDeleteBin2Fill fontSize={18} />
							</Button>
						</>
					}

					<Button
						color="primary"
						disabled={columnIndex === -1}
						style={{ borderRadius: "0px 4px 4px 0px", fontSize: 18, padding: "0px 24px" }}
						onClick={() => addTags()}
					>
						+
					</Button>
				</InputGroup>
				{props.children}				
			</div>
			<div>
				{errorCode > -1 && <span style={{ color: "red" }}>{errors[errorCode]}</span>}
				{Object.keys(tags).filter(columnName => tags[columnName].length > 0).length > 0 && <hr />}
				
				<ul id="tags" style={{ padding: '0px', marginTop: "1rem" }}>
					{
						Object.keys(tags).filter(columnName => tags[columnName].length > 0).map(columnName => (
							<li style={{ display: "inline-block", paddingRight: "20px" }}>
								<Tag column={columnName} filters={tags[columnName]} close={removeTags} />
							</li>
						))
					}
				</ul>
			</div>
		</div>
	);
};
