import React from "react";


export const LoginContext = React.createContext();

export function LoginProvider({ children }) {
  const [isAuditorsDataReadyState, setIsAuditorsDataReadyState] = React.useState(false);
  const [isGroupsDataReadyState, setIsGroupsDataReadyState] = React.useState(false);
  
    
    return (
        <LoginContext.Provider value={{isAuditorsDataReadyState, isGroupsDataReadyState, setIsAuditorsDataReadyState, setIsGroupsDataReadyState}}>
            {children}
        </LoginContext.Provider>
    );
}
