import React from "react";
import {
    Button, Col,
} from 'reactstrap';

import { FiPauseCircle, FiPlayCircle } from "react-icons/fi";
import MODAL_TYPE_ENUM from "common/enumerators/ModalTypeEnum";
import { isEmpty } from "common/utils/collections";




export default function PauseButton({modal, data, pauseState, isDisabled, pauseAction, resumeAction, view}) {


    let warningConfigObj = data
    
    if (!warningConfigObj || isEmpty(warningConfigObj.getIdConfig()))
        return ""
    let pauseDate = new Date(warningConfigObj.getPauseTimestamp()); 
    let now = new Date();

    let icon, label = null

    if (typeof pauseState !== 'undefined' && pauseState === true) {
        icon = <FiPlayCircle size={20} style={{ marginRight: '5px' }} />
        label = 'Reativar'        
    } else if (typeof pauseState !== 'undefined' && pauseState === false) {
        icon = <FiPauseCircle size={20} style={{ marginRight: '5px' }} />
        label = 'Suspender'        
    } else if (pauseDate && pauseDate > now) {
        icon = <FiPlayCircle size={20} style={{ marginRight: '5px' }} />
        label = 'Reativar'        
    } else {
        icon = <FiPauseCircle size={20} style={{ marginRight: '5px' }} />
        label = 'Suspender'        
    }
    

    const action = () => {
        if (typeof pauseState !== 'undefined' && pauseState === true) {
            if(view || modal) resumeAction(warningConfigObj);
            else resumeAction(MODAL_TYPE_ENUM.WARNING_PAUSE)
        } else if (typeof pauseState !== 'undefined' && pauseState === false) { 
            if(view || modal) pauseAction(warningConfigObj);
            else pauseAction(MODAL_TYPE_ENUM.WARNING_PAUSE)
        } else if (pauseDate && pauseDate > now) {
            if(view || modal) resumeAction(warningConfigObj);
            else resumeAction(MODAL_TYPE_ENUM.WARNING_PAUSE)
        } else {
            if(view || modal) pauseAction(warningConfigObj);
            else pauseAction(MODAL_TYPE_ENUM.WARNING_PAUSE)
        }
    }
    
    return (            
        <Col >
            <Button
                size={view  ? "sm" : ""}
                //color={view? 'secondary' : 'primary'}
                disabled={isDisabled(warningConfigObj.getCreator())}
                onClick={() => { action() }}
                style={(!view || modal) ? { width: "9.5rem", height:"2.5rem", marginRight: "60px" } : {height: "2.0rem", width: "6.5rem", marginTop: "3px"}}
                >
                {(!view || modal) && icon} 
                {label}
            </Button>
        </Col>
    );


}
