import React from "react";
import {
    Button, Col,
} from 'reactstrap';



export default function EditionButtons({ data, id, creator, isModal, setModalReadonlyMode, isDisabled, editionAction, deleteAction  }) {


    const action = () => {
        if (isModal) {
            setModalReadonlyMode(false)
        } else {
            editionAction(data, false) 
        }
    }
    
    const getEditonStyle = () => {
        let style = isModal ? { width: "9.5rem", height:"2.5rem" } : { width: "6.0rem" }
        if (isModal){
            style['marginLeft'] = "60px"
        }        
        return style
        
    }

    const getExclusionStyle = () => {
        let style = isModal ? { width: "9.5rem", height:"2.5rem" } : { width: "6.0rem" }
        if (!isModal){
            style['marginTop'] = "3px"
        }
        return style
    }

    
    return (            
        <>
        <Col >
          <Button
            color='primary'
            disabled={isDisabled(creator)}//caso esteja associado a tela de config ou de grupos somente o criador/admin pode editar um cadastro
            onClick={() => { action() }}
            style={getEditonStyle()}              
          >
            Editar
          </Button>
        </Col>
        
        <Col>
          <Button
            color='danger'
            disabled={isDisabled(creator)}//caso esteja associado a tela de config ou de grupos somente o criador/admin pode editar um cadastro
            style={getExclusionStyle()}              
            onClick={() => { deleteAction(id) }}
          >
            Excluir
          </Button>
        </Col>
        </>    
    );


}
