import React from "react"
import { RiCloseCircleFill } from "react-icons/ri"
import { CgArrowLongRightL } from "react-icons/cg"
import { v4 } from 'uuid'

const Tag = ({ column, filters, close, readOnly=false }) => {
  return (
    <div key={v4()} style={{ marginBottom: "10px" }}>
      <span key={v4()}
        style={{
          height: 30,
          backgroundColor: "#51cbce",
          color: "#fff",
          display: "inline-block",
          padding: "3px 5px 3px 10px",
          fontWeight: "bold",
          borderRadius: "5px 0 0 5px",
          border: "1px #DDDDDD",
          borderStyle: "solid none solid solid",
        }}
      >
        {column}
      </span>
      <span key={v4()}
        style={{
          height: 30,
          backgroundColor: "#fff",
          // fontWeight: "bold",
          border: "1px #DDDDDD",
          borderStyle: "solid solid solid none",
          color: "#444",
          display: "inline-block",
          padding: "3px 5px 3px 5px",
          borderRadius: "0 5px 5px 0"
        }}
      >
        {filters.map((filter, index) => (
          <span key={v4()} >
            {filter.constructor === Array?
            <span key={v4()}>{filter[0]} <CgArrowLongRightL style={{margin: "0px 1px", fontSize: 20, position: "relative", bottom: 1}}/> {filter[1]}</span>
            :
            filter
            }

            {!readOnly && <RiCloseCircleFill
              style={{ cursor: "pointer", color: "#51cbce", marginLeft: "3px" }}
              onClick={()=>close(column, index)}
            />}

            {index+1 !== filters.length &&
              <span key={v4()}
              style={{
                backgroundColor: "#66615B",
                color: "#fff",
                fontWeight: "normal",
                padding: "5px 5px",
                margin: "0px 5px",
                border: "1px #DDDDDD",
                borderStyle: "none solid none solid",
                
              }}
              >
                ou
              </span>
            }
          </span>
        ))}
      </span>
    </div>
  );
};

export default Tag;
