import React from "react";
import { ViewsContext } from "context/ViewsContext";
import TagsInput from "components/tags/TagsInput";
import Table from "components/tables/Table";
import { createQueryHttpRequestBody } from "axios/AxiosUtils";
import Plate from "components/table-elements/Plate";
import { TrafficTimestamp } from "components/table-elements/TrafficTimestamp";
import TrafficLastLocations from "components/table-elements/TrafficLastLocations";
import { EVENT_CREATION_TIMESTAMP } from "common/model/Event";
import { EVENT_LOCAL } from "common/model/Event";
import { EVENT_VEHICLE_PLATE } from "common/model/Event";
import { EVENT_VEHICLE_TYPE } from "common/model/Event";
import { EVENT_VEHICLE_MODEL } from "common/model/Event";
import { EVENT_VEHICLE_COLOUR } from "common/model/Event";
import { EVENT_UF } from "common/model/Event";
import { createSimpleFilterClause } from "axios/AxiosUtils";
import { createOrderByClause } from "axios/AxiosUtils";
import { EVENT_DFE_TYPE } from "common/model/Event";
import { trafficNoDfeFiltersOptions } from "../utils/filters";
import { useModalWithData } from "common/hooks/useModalWithData";
import MODAL_TYPE_ENUM from "common/enumerators/ModalTypeEnum";
import VehicleModal from "components/modals/VehicleModal";
import { CHAR } from "axios/AxiosUtils";
import { EQUAL } from "axios/AxiosUtils";
import { DESC } from "axios/AxiosUtils";
import { cloneFilters } from "axios/AxiosUtils";
import { WITHOUT_DFE } from "common/model/Event";
import { EVENT_INGESTION_TIMESTAMP } from "common/model/Event";


//TODO remover estas referencias a groupsConditons e groupsValues. Isso deve vir do context
export default function TrafficNoDfeTable({toggleMapModal}) {


  const { refreshFlag, withoutDfeTableFilters, setWithoutDfeTableFilters,withoutDfeTableTags, setWithoutDfeTableTags } = React.useContext(ViewsContext)

  const [isLoading, setIsLoading] = React.useState(false)
  const { modalData, isModalActive, modalType, toggleModalOn, toggleModalOff } = useModalWithData()


  const createTrafficHttpRequestBody = () => {
    let filtersClone = cloneFilters(withoutDfeTableFilters)
    filtersClone.push(createSimpleFilterClause(EVENT_DFE_TYPE, CHAR, EQUAL, WITHOUT_DFE))

    const orderBy = createOrderByClause(EVENT_INGESTION_TIMESTAMP, DESC)
    return createQueryHttpRequestBody(filtersClone, orderBy)
  }


  return (<>
    <TagsInput
      setQuery={setWithoutDfeTableFilters}
      tagsState={[withoutDfeTableTags, setWithoutDfeTableTags]}
      columns={trafficNoDfeFiltersOptions}
    />
    <Table
      dataUrl="/events/read_all"
      timeout={10000}
      requestBody={createTrafficHttpRequestBody}
      tags={withoutDfeTableFilters}
      fillData
      refresh={refreshFlag}
      loading={[isLoading, setIsLoading]}
      invalid={["null", undefined, null, "", "-"]}
      columns={
        {
          "Passagem": {
            args: [EVENT_CREATION_TIMESTAMP, EVENT_INGESTION_TIMESTAMP],
            Func: ({ data }) => <TrafficTimestamp data={data} isTrafficTimestamp={true} />
          },
          "Recebimento": {
            args: [EVENT_CREATION_TIMESTAMP, EVENT_INGESTION_TIMESTAMP],
            Func: ({ data }) => <TrafficTimestamp data={data} />
          },
          "Local": {
            args: EVENT_LOCAL,
            Func: ({ row_data }) => <TrafficLastLocations data={row_data}  toggleModal={toggleMapModal}/>
          },
          "Uf Disparo": {
            args: EVENT_UF,
            thStyle: { textAlign: 'center' },
            tdStyle: { textAlign: 'center' },
          },
          "Placa": {
            args: EVENT_VEHICLE_PLATE,
            thStyle: { textAlign: 'center' },
            tdStyle: { textAlign: 'center' },
            func: (plate) => <Plate plate={plate} toggleModal={toggleModalOn} />
          },
          "Veículo": EVENT_VEHICLE_TYPE,
          "Modelo": EVENT_VEHICLE_MODEL,
          "Cor": EVENT_VEHICLE_COLOUR,
        }
      }
    />
       {modalType === MODAL_TYPE_ENUM.VEHICLE && <VehicleModal show={isModalActive} toggle={toggleModalOff} data={modalData} />}
  </>)
}
