import React from "react";

import {
  Row,
  Input,
  Col,
} from 'reactstrap';


import { EVENT_CREATION_TIMESTAMP } from "common/model/Event";
import { EVENT_LOCAL } from "common/model/Event";
import { EVENT_VEHICLE_TYPE } from "common/model/Event";
import { EVENT_VEHICLE_MODEL } from "common/model/Event";
import { EVENT_VEHICLE_COLOUR } from "common/model/Event";
import { EVENT_VEHICLE_PLATE } from "common/model/Event";
import { DESCRIPTION } from "common/model/WarningConfig";
import { FISCAL_ACTION } from "common/model/WarningConfig";
import { calculateComponentHeight } from "common/utils/forms";
import { convertUtcToBrazilianDate } from "common/utils/validate";
import { manageInputTables } from "common/utils/collections";

export default function EventPanel({ data }) {


  return (
    <>

      <Row style={{ marginTop: '10px' }}>
        <Col>
          <b>Data/Hora:</b>
          {` ${convertUtcToBrazilianDate(manageInputTables(data, [EVENT_CREATION_TIMESTAMP]))}`}
        </Col>
      </Row>
      <Row style={{ marginTop: '5px' }}>
        <Col>
          <b>Local:</b>
          {` ${manageInputTables(data, [EVENT_LOCAL])}`}
        </Col>
      </Row>
      <Row style={{ marginTop: '5px' }}>
        <Col>
          <b>Veículo:</b>
          {` ${manageInputTables(data, [EVENT_VEHICLE_TYPE])} / ${manageInputTables(data, [EVENT_VEHICLE_MODEL])} / ${manageInputTables(data, [EVENT_VEHICLE_COLOUR])}`}
        </Col>
      </Row>
      <Row style={{ marginTop: '5px' }}>
        <Col>
          <b>Placa:</b>
          {` ${manageInputTables(data, [EVENT_VEHICLE_PLATE])}`}
        </Col>
      </Row>
      <Row style={{ marginTop: '5px' }}>
        <Col>
          <b>Descrição:</b>
        </Col>
        <Input
          type='textarea'
          readOnly
          value={manageInputTables(data, [DESCRIPTION])}
          maxLength="2048"
          style={calculateComponentHeight(manageInputTables(data, [DESCRIPTION]), "0 14px")}
        />
      </Row>
      <Row style={{ marginTop: '5px' }}>
        <Col>
          <b>Ação Fiscal:</b>
        </Col>
        <Input
          type='textarea'
          readOnly
          value={manageInputTables(data, [FISCAL_ACTION])}
          maxLength="2048"
          style={calculateComponentHeight(manageInputTables(data, [FISCAL_ACTION]), "0 14px")}
        />
      </Row>
    </>)
}
