import React from 'react';

import {
Col, Row,
  Button,
} from 'reactstrap';


import PauseButton from 'components/buttons/PauseButton';
import { isAdmin } from 'common/SessionUtils';
import { isSubgerente } from 'common/SessionUtils';
import { isMonitoramento } from 'common/SessionUtils';
import { HOMOLOGATION_MODE } from 'components/modals/HomologationModal';
import { SUPERVISION_MODE } from 'components/modals/HomologationModal';
import { AWARENESS_MODE } from 'components/modals/HomologationModal';

export const NotificationActionButtonsProps = { 
    mode : {
      editionMode : true,
      popHomologatedNotificationEnabled : false
    },
    data : {
      notificationObj : null,
      notificationIndex : null
    },
    actionMethods :{
      editionMethod : null,
      homologationMethod : null,
      suspensionMethod : null,
      reactivationMethod : null,
      warningClosingMethod : null,
    },
    disabledMethod : {
      isEditionButtonDisabled : null,
      isPauseButtonDisabled : null
    }

  };

export const EDITION_MODE = 'editionMode'
export const POP_TREATED_NOTIFICATIONS = 'popHomologatedNotificationEnabled'
export const NOTIFICATION_OBJ = 'notificationObj'
export const RAW_DATA = 'raw_data'
export const NOTIFICATION_INDEX = 'notificationIndex'
export const EDITION_METHOD = 'editionMethod'
export const HOMOLOGATION_METHOD = 'homologationMethod'
export const SUSPENSION_METHOD = 'suspensionMethod'
export const REACTIVATION_METHOD = 'reactivationMethod'
export const WARNING_CLOSE_METHOD = 'warningClosingMethod'
export const EDITION_IS_DISABLED_METHOD = 'isEditionButtonDisabled'
export const PAUSE_IS_DISABLED_METHOD = 'isPauseButtonDisabled'


export function NotificationActionButtons({ mode, data, actionMethods, disabledMethods }) {

  
  const raw_data = data[RAW_DATA]
  const notificationIndex = data[NOTIFICATION_INDEX]
  const notificationObj = data[NOTIFICATION_OBJ]
  const warningConfigObj = notificationObj.getWarningConfig()
  const editionMode = mode[EDITION_MODE]
  const popHomologatedNotificationEnabled = mode[POP_TREATED_NOTIFICATIONS]
  const onEditionClick = actionMethods[EDITION_METHOD]
  const onHomologationClick = actionMethods[HOMOLOGATION_METHOD]
  const onSuspensionClick = actionMethods[SUSPENSION_METHOD]
  const onReactivationClick = actionMethods[REACTIVATION_METHOD]
  const onWarningClosingClick = actionMethods[WARNING_CLOSE_METHOD]
  const isEditionButtonDisabled = disabledMethods[EDITION_IS_DISABLED_METHOD]
  const isPauseButtonDisabled = disabledMethods[PAUSE_IS_DISABLED_METHOD]

  return (
    <Row>
      {editionMode && <Col>
        <Button size="sm" className='btn btn-primary' 
          style={{ height: "2.0rem", width: "6.5rem", marginTop: "3px" }} outline 
          disabled={isEditionButtonDisabled(notificationObj)} 
          onClick={() => { onEditionClick(notificationObj, true) }}
          >
            Editar
        </Button>
      </Col>}

      {!editionMode && (isAdmin() || isSubgerente()) && notificationObj.isHomologationPending() && <Col>
        <Button size="sm" className='btn btn-success' 
          style={{ height: "2.0rem", width: "6.5rem", marginTop: "3px" }} outline 
          onClick={() => { onHomologationClick(HOMOLOGATION_MODE, notificationObj, notificationIndex, popHomologatedNotificationEnabled, raw_data); }}
          >
            Homologar
        </Button>
      </Col>}

      {!editionMode && (isAdmin() || isSubgerente()) && notificationObj.isSupervisionPending() && <Col>
        <Button size="sm" className='btn btn-info'
          style={{ height: "2.0rem", width: "6.5rem", marginTop: "3px" }} outline 
          onClick={() => { onHomologationClick(SUPERVISION_MODE, notificationObj, notificationIndex, popHomologatedNotificationEnabled, raw_data); }}
          >
            Supervisão
        </Button>
      </Col>}


      {!editionMode && (isAdmin() || isSubgerente()) && notificationObj.isAwarenessPending() && <Col>
        <Button size="sm" className='btn btn-warning'
          style={{ height: "2.0rem", width: "6.5rem", marginTop: "3px" }} outline 
          onClick={() => { onHomologationClick(AWARENESS_MODE, notificationObj, notificationIndex, popHomologatedNotificationEnabled, raw_data); }}
          >
            Ciência
        </Button>
      </Col>}

      
      {editionMode && (isAdmin() || isSubgerente() || isMonitoramento()) && editionMode && !warningConfigObj.isConfigClosed() &&  <Col>
        <PauseButton view={true} data={warningConfigObj} 
          isDisabled={isPauseButtonDisabled} 
          pauseAction={onSuspensionClick} resumeAction={onReactivationClick} 
        />
        
      </Col>}

      {editionMode && (isAdmin() || isSubgerente())  &&  !warningConfigObj.isConfigClosed() && <Col>
        <Button size="sm"  className='btn btn-danger' 
          style={{ height: "2.0rem", width: "6.5rem", marginTop: "3px" }} outline 
          onClick={() => { onWarningClosingClick(warningConfigObj, notificationIndex, popHomologatedNotificationEnabled); }}
          >
            Encerrar Alerta
        </Button>
      </Col>
      }

    </Row>
  )
}
