import React from "react"

export const ViewsContext = React.createContext()

export function ViewsProvider({ children }) {
  const [refreshFlag, setRefreshFlag] = React.useState(false)
  const [warningTableFilters, setWarningTableFilters] = React.useState([])
  const [dfeTableFilters, setDfeTableFilters] = React.useState([])
  const [withoutDfeTableFilters, setWithoutDfeTableFilters] = React.useState([])
  const [pendingNewNotifications, setPendingNewNotifications] = React.useState(false)
  const [nfeIssuanceFilters, setNfeIssuanceFilters] = React.useState([])
  const [mdfeIssuanceFilters, setMdfeIssuanceFilters] = React.useState([])
  const [notificationFilters, setNotificationFilters] = React.useState([])
  
  const [warningsFilters, setWarningsFilters] = React.useState([])
  const [auditorsFilters, setAuditorsFilters] = React.useState([])
  const [groupsFilters, setGroupsFilters] = React.useState([])


  const [warningsTags, setWarningsTags] = React.useState({})
  const [notificationTags, setNotificationTags] = React.useState({})
  const [warningTableTags, setWarningTableTags] = React.useState({})
  const [dfeTableTags, setDfeTableTags] = React.useState({})
  const [withoutDfeTableTags, setWithoutDfeTableTags] = React.useState({})



  const refresh = () => {setRefreshFlag(!refreshFlag)}
 

  return (
    <ViewsContext.Provider
      value={{
        refreshFlag, refresh,
        warningTableFilters, setWarningTableFilters,
        dfeTableFilters, setDfeTableFilters,
        withoutDfeTableFilters, setWithoutDfeTableFilters,
        pendingNewNotifications, setPendingNewNotifications,

        //TODO REMOVER ESTA GAMB. Aqui deve salvar o filtro em si, ao invés dos valores exibidos pelas tags. 
        warningTableTags, setWarningTableTags,
        dfeTableTags, setDfeTableTags,
        withoutDfeTableTags, setWithoutDfeTableTags,
        notificationTags, setNotificationTags,
        warningsTags, setWarningsTags,
        

        nfeIssuanceFilters, setNfeIssuanceFilters,
        mdfeIssuanceFilters, setMdfeIssuanceFilters,
        notificationFilters, setNotificationFilters,
        warningsFilters, setWarningsFilters,
        auditorsFilters, setAuditorsFilters,
        groupsFilters, setGroupsFilters
      }}>
      {children}
    </ViewsContext.Provider>
  );
}
