import USER_ID_ENUM from "common/enumerators/UserIdEnum";
import { calculateComponentHeight } from "common/utils/forms";
import { AuditorsContext } from "context/AuditorsContext";

import React from "react";
import {
  Form,
  FormGroup,
  Label,
  Input,  
  Modal,
  ModalBody,
  ModalHeader,
  
} from 'reactstrap';




export default function GroupModal({ show, toggle, data}) {

  const { getAuditorRegistry } = React.useContext(AuditorsContext);

  function mountGroupMembersText(groupMembers, owner) {
    let result = [];
    for (const user of groupMembers) {
      let owner = data['group_owner']
      let userRegistry = getAuditorRegistry(user['user_id'], USER_ID_ENUM.USER_ID)
      if (userRegistry) {
        var text = userRegistry['user_name'] + "(" + userRegistry['user_registration_id'] + ")"
        if (owner === userRegistry['user_login']) {
          text = text + " *****"
        }

        result.push(text.toUpperCase())
      } else {
        result.push(("Usuário: " + user['user_id']).toUpperCase())
      }
    }
    return result.sort().join("\n")
  }
  
  
  return <Modal isOpen={show} toggle={toggle} size={"lg"}>
            <ModalHeader toggle={toggle}>Grupo Operacional:{data['group_name']}</ModalHeader>
            <ModalBody>
              <Form style={{ padding: '1.5rem' }}>

                <FormGroup row>
                  <Label for="exampleEmail" style={{ marginRight: '3px' }}><b>Descrição:</b></Label>
                  <Input type='textarea' readOnly
                    value={data['group_description']}
                    maxLength="1024" style={calculateComponentHeight(data['group_description'])} ></Input>
                </FormGroup>

                <FormGroup row>
                  <Label for="exampleEmail" style={{ marginRight: '3px' }}><b>Membros:</b></Label>
                  <Input type='textarea' readOnly
                    value={mountGroupMembersText(data['usuario_grupo'], data)}
                    maxLength="1024" style={calculateComponentHeight(mountGroupMembersText(data['usuario_grupo'], data))} ></Input>
                </FormGroup>

              </Form>
            </ModalBody>
          </Modal>;
}
