import { cpfFilter, cnpjFilter, dateFilter } from 'components/tags/filterPresets'
import { priorityFilter, moneyFilter, integerFilter, configTypeFilter, ownWarningConfigFilter } from 'components/tags/filterPresets'

export const TAG_INPUT_OPTIONS = {
    "Placa": {
      field: PLATE,
