    },
    "Valor Agregado de DFes": {
      field: AGREGATED_LOWER_VALUE,
      only_numbers_filter_input: false,
      ...moneyFilter
    },
  }
  

  export const WARNING_CONFIG_FILTERS = (groupsOptions)  => {return {
    "Prioridade": {
      field: SEVERITY,
      ...priorityFilter
    },
