import TableLoading from "./TableLoading"
import { RiNotification2Line } from "react-icons/ri";
import { MDFE_KEY } from "common/model/Mdfe";
import { MDFE_EMIT_CNPJ } from "common/model/Mdfe";
import { MDFE_EMIT_NAME } from "common/model/Mdfe";
import { MDFE_VOLUME_CARGO } from "common/model/Mdfe";
import { MDFE_VOLUME_UNIT } from "common/model/Mdfe";
import { MDFE_TOTAL_VALUE } from "common/model/Mdfe";
import { MDFE_STATUS } from "common/model/Mdfe";
import { getMdfeATFLink } from "common/enviroment";
import { MDFE_DHEMI } from "common/model/Mdfe";
import { MDFE_PLATE } from "common/model/Mdfe";
import { Col, Label, Row } from "reactstrap";
import { MDFE_ORIGIN } from "common/model/Mdfe";
import { MDFE_DESTINATION } from "common/model/Mdfe";
import { convertUtcToBrazilianDate } from "common/utils/validate";
import { sortData } from "common/utils/collections";
import { manageInputTables } from "common/utils/collections";
import { formatCnpjCpf } from "common/utils/validate";
import { isEmpty } from "common/utils/collections";

export default function MdfeTable({ data, headerText, mdfeOfInterest, loading, showBell = false, showPlate = false }) {

    return <div className="content">
        <Row>
            <Col md="12">
                <Label style={{ marginRight: '5px' }}><b>{headerText}</b></Label>
            </Col>
        </Row>
        <Row>
            <Col md="12">
                <TableLoading loading={loading}>
                    <thead className="text-primary">
                        <tr>
                            {showBell && <th></th>}
                            <th>#</th>
                            <th style={{ textAlign: 'center' }}>Data Emissão</th>
                            <th style={{ textAlign: 'right' }}>Emitente</th>
                            <th style={{ textAlign: 'right' }}>Nome do emitente</th>
                            {showPlate && <th  style={{ textAlign: 'center' }}>Placa</th>}
                            <th style={{ textAlign: 'center' }}>UF Origem</th>
                            <th style={{ textAlign: 'center' }}>UF Destino</th>
                            <th style={{ textAlign: 'center' }}>Peso Carga</th>
                            <th style={{ textAlign: 'right' }}>Valor</th>
                            <th style={{ textAlign: 'center' }}>Situação</th>
                            <th  style={{ textAlign: 'center' }}>Chave da mdfe</th>
                        </tr>
                    </thead>
                    <tbody>
                        {!isEmpty(data) && sortData(data, MDFE_TOTAL_VALUE).map((mdfeData, index) => {
                            return (
                                <tr key={index}>
                                    {showBell &&
                                        <td style={{ paddingBottom: '0px' }}>{
                                            mdfeOfInterest === mdfeData[MDFE_KEY] ? <p><RiNotification2Line size={15} /></p> : ''}
                                        </td>
                                    }
                                    <td>{index}</td>
                                    <td style={{ textAlign: 'center' }}>{convertUtcToBrazilianDate(mdfeData[MDFE_DHEMI])}</td>
                                    <td style={{ textAlign: 'right' }}>{formatCnpjCpf(mdfeData[MDFE_EMIT_CNPJ])}</td>
                                    <td style={{ textAlign: 'right' }}>{manageInputTables(mdfeData, [MDFE_EMIT_NAME])}</td>
                                    {showPlate && <td style={{ textAlign: 'center' }}>{manageInputTables(mdfeData, [MDFE_PLATE])}</td>}
                                    <td style={{ textAlign: 'center' }}>{manageInputTables(mdfeData, [MDFE_ORIGIN])}</td>
                                    <td  style={{ textAlign: 'center' }}>{manageInputTables(mdfeData, [MDFE_DESTINATION])}</td>
                                    <td style={{ textAlign: 'right' }}>{manageInputTables(mdfeData, [MDFE_VOLUME_CARGO]).toLocaleString('pt-br', { minimumFractionDigits: 2 }).replace(",00", "")} {`(${manageInputTables(mdfeData, [MDFE_VOLUME_UNIT]) === "01" ? "kg" : "t"})`}</td>
                                    <td style={{ textAlign: 'right' }}>R${` ${manageInputTables(mdfeData, [MDFE_TOTAL_VALUE]).toLocaleString('pt-br', { minimumFractionDigits: 2 })}`}</td>
                                    <td  style={{ textAlign: 'center' }}>{manageInputTables(mdfeData, [MDFE_STATUS])}</td>
                                    <td style={{ textAlign: 'center' }}><a style={{ color: '#2322c8' }}
                                        href={getMdfeATFLink(manageInputTables(mdfeData, [MDFE_KEY]))}


                                        target="_blank" rel="noreferrer">{manageInputTables(mdfeData, [MDFE_KEY])}</a></td>
                                </tr>
                            )
                        })}
                    </tbody>
                </TableLoading>
            </Col>
        </Row>
    </div>


}
