import React from 'react';
import {
    Button,
    Card,
    CardTitle,
} from "reactstrap";
import { HiPrinter } from "react-icons/hi";
import SEVERITY_ENUM from '../common/enumerators/SeverityEnum'
import { getNotificationStatusLabel } from 'common/enumerators/NotificationStatus';
import NOTIFICATION_STATUS_ENUM from 'common/enumerators/NotificationStatus';
import { convertUtcToBrazilianDate } from 'common/utils/validate';
import { isEmpty } from 'common/utils/collections';
import { compareLastTwoLogs, checkIfKeyIsInDiff } from 'common/rules/NotificationRules';
import logo from "logo.svg";

const NotificationHistoryLayout = () => { // aqui
    const historyData = JSON.parse(sessionStorage.getItem('historyData'));
    const warning = JSON.parse(sessionStorage.getItem('warning'));
    const [pageLoadDate, setPageLoadDate] = React.useState(null);
    React.useEffect(() => {
        const currentDate = new Date();
        setPageLoadDate(currentDate.toLocaleDateString());
    }, []);

    const handlePrint = () => {
        const content = document.querySelector('.content');
        const printWindow = window.open('', '_blank');
        if (printWindow) {
            printWindow.document.write('<html><head>');
        
            // Copiar estilos CSS
            const stylesheets = document.querySelectorAll('link[rel="stylesheet"]');
            stylesheets.forEach((stylesheet) => {
                printWindow.document.write(`<link rel="stylesheet" href="${stylesheet.href}">`);
            });
        
            printWindow.document.write('</head><body>');
        
            // Copiar conteúdo da página, incluindo imagens
            printWindow.document.write(document.documentElement.outerHTML);
        
            printWindow.document.write('</body></html>');
            printWindow.document.close();
        
            // Aguardar até que o conteúdo seja totalmente carregado antes de imprimir
            printWindow.addEventListener('load', () => {
                printWindow.print();
            });
        }
    };

    const logDiffs = compareLastTwoLogs(historyData);

    console.log("DIFFS ", logDiffs)
    
    const strongStyle = { marginRight: '1px', marginLeft: '8px' };

    const getStyleDiff = (key, i) => {
        const log = i;
        
        const previousLog = historyData[historyData.indexOf(log) - 1];
        if (previousLog) {
            if (checkIfKeyIsInDiff(key, previousLog, log, logDiffs)) {
                return { color: '#B80000', ...strongStyle };
            }
        }
        return strongStyle;
    }

    return (
        <>
            <header style={{ textAlign: 'center', marginBottom: '20px' }}>
                <div style={{display: 'flex', alignItems: 'center', padding: '20px', marginLeft: '20px'}}>
                <img style={{width: '100px', height: 'auto', marginRight: '10px'}} src={logo} alt="cicc-logo" />
                <h2 style={{position:'relative',top: '20px'}}>Histórico de Configuração de Notificação</h2>
                </div>
            </header>
            <div className="content" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', marginTop: '10px', position: 'relative' }}>
                {!isEmpty(warning) && <Card body key={-1} style={{ width: "95%" }}>
                <CardTitle>{ `Ocorrência ${warning.id} `} </CardTitle>
                
                    <p>
                        <strong style={strongStyle}>Nº Alerta: </strong> {warning.id_config ?? ""} <br/>
                        <strong style={strongStyle}>Nome: </strong> {warning.nome ?? ""} <br/>
                        <strong style={strongStyle}>Descrição: </strong> {warning.descricao ?? ""} <br/>
                        <strong style={strongStyle}>Prioridade: </strong> {SEVERITY_ENUM[warning.severidade] ?? ""} <br/>
                        <strong style={strongStyle}>Ação Fiscal: </strong> {warning.procedimento ?? ""} <br/>
                    </p>                                
                    <p>
                        <strong style={strongStyle}>Data/Hora da Passagem:</strong> {convertUtcToBrazilianDate(warning.evento_infevento_dhregpassagem) ?? ''}<br/>
                        <strong style={strongStyle}>Local: </strong> {warning.evento_infevento_dsreflocal}<br/>
                        <strong style={strongStyle}>Veículo: </strong> {warning.evento_infevento_nrplaca}<br/>
                    </p>
                    </Card>
                }
                    
                { !isEmpty(historyData) && historyData.length > 0 && historyData.map(i => {
                    const status = i.fiscal_notification_status
                    let statusLabel = getNotificationStatusLabel(i)
                    if (status === NOTIFICATION_STATUS_ENUM.VISUALIZADA && i['fiscal_notification_lock'] === 1){
                        statusLabel = "ALERTA BLOQUEADO"
                    } 
                    
                    if (status === NOTIFICATION_STATUS_ENUM.VISUALIZADA && i['fiscal_notification_lock'] === 2){
                        statusLabel = "ALERTA DESBLOQUEADO"
                    }
                    
                    return (
                        <Card body key={i.id_config} style={{ width: "95%" }}>
                            <CardTitle>Modificado às <strong> {i.log_timestamp !== null ? convertUtcToBrazilianDate(i.log_timestamp) : ""}</strong></CardTitle>
                            <p>
                                <strong style={getStyleDiff("fiscal_notification_status",i)}>Status: </strong> {statusLabel}
                            </p>

                            { !isEmpty(i.fiscal_action_designation_auditor) &&
                                <p>
                                    <strong style={getStyleDiff("fiscal_action_designation_auditor",i)}>Responsável pelo Monitoramento: </strong> {i.fiscal_action_designation_auditor}<br/>
                                    <strong style={getStyleDiff("fiscal_action_designation_date",i)}>Data/Hora da Designação: </strong> {i.fiscal_action_designation_date}<br/>
                                    <strong style={getStyleDiff("fiscal_action_auditors",i)}>Fiscais Designados: </strong> {i.fiscal_action_auditors}<br/>
                                    <strong style={getStyleDiff("fiscal_action_designation_report",i)}>Observações da Designação: </strong> {i.fiscal_action_designation_report}<br/>
                                </p>
                            }
                            { !isEmpty(i.fiscal_action_designation_auditor) &&
                                <p>
                                    <strong style={getStyleDiff("fiscal_notification_awareness_auditor",i)}>Responsável pela Declaração de Ciência: </strong> {i.fiscal_notification_awareness_auditor}<br/>
                                    <strong style={getStyleDiff("fiscal_notification_awareness_date",i)}>Data/Hora da Declaração de Ciência: </strong> {i.fiscal_notification_awareness_date}<br/>
                                    <strong style={getStyleDiff("fiscal_notification_awareness_report",i)}>Observações da Declaração de Ciência: </strong> {i.fiscal_notification_awareness_report}<br/>
                                </p>
                            }
                            { !isEmpty(i.fiscal_action_report) &&
                                <p>
                                    <strong style={getStyleDiff("fiscal_action_date",i)}> Data/Hora da Ação Fiscal Realizada: </strong> {i.fiscal_action_date}<br/>
                                    <strong style={getStyleDiff("fiscal_action_report",i)}> Relatório da Ação Fiscal Realizada: </strong> {i.fiscal_action_report}<br/>
                                    <strong style={getStyleDiff("fiscal_action_report_auditor",i)}> Responsável pelo Preenchimento do Relatório da Ação Fiscal Realizada: </strong> {i.fiscal_action_report_auditor}<br/>
                                </p>
                            }
                            { !isEmpty(i.fiscal_action_homologation_auditor) &&
                                <p>
                                    <strong style={getStyleDiff("fiscal_action_homologation_auditor",i)}>Responsável pela Homologação: </strong> {i.fiscal_action_homologation_auditor}<br/>
                                    <strong style={getStyleDiff("fiscal_action_homologation_date",i)}>Data/Hora da Homologação: </strong> {i.fiscal_action_homologation_date}<br/>
                                    <strong style={getStyleDiff("fiscal_action_homologation_report",i)}>Observações da Homologação: </strong> {i.fiscal_action_homologation_report}<br/>
                                </p>      
                            }
                            {i.config_pause_timestamp && i.config_pause_timestamp !== null && i.config_pause_timestamp !== '' && i.config_status === NOTIFICATION_STATUS_ENUM.PAUSED &&
                                <p>
                                    <strong style={getStyleDiff("config_pause_timestamp",i)}> Alerta pausado até: </strong> {convertUtcToBrazilianDate(i.config_pause_timestamp)}<br/>
                                    <strong style={getStyleDiff("config_pause_report",i)}> Motivo: </strong> {i.config_pause_report}<br/>
                                </p>
                            }          
                            {i.config_pause_timestamp && i.config_pause_timestamp !== null && i.config_pause_timestamp !== '' && i.config_status === NOTIFICATION_STATUS_ENUM.ACTIVE &&
                                <p>
                                    <strong style={getStyleDiff("config_pause_timestamp",i)}> Alerta reativado em: </strong> {convertUtcToBrazilianDate(i.config_pause_timestamp)}<br/>
                                </p>
                            }            
                        </Card>
                    );
                })}

            </div>
            <div style={{ position: 'fixed', bottom: '15px', right: '10px', zIndex: 1 }}>
                <Button style={{ borderRadius: '50%', backgroundColor: '#007bff', padding: '15px', border: 'none', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)' }} size="md" id="print-button" onClick={handlePrint}><HiPrinter style={{ fontSize: '3em' }} /></Button>
            </div>
            <footer style={{ textAlign: 'center', marginTop: '20px' }}>
                <p>Página aberta em: {pageLoadDate}</p>
            </footer>
        </>
    );
};

export default NotificationHistoryLayout;
