import React from "react";

import {
  Row,
  Input,
  Col,
} from 'reactstrap';

import { DESIGNATION_AUDITOR } from "common/model/Notification";
import { MONITORING_USER_LOGIN } from "common/model/Notification";
import { MONITORING_USER_REGISTRATION_ID } from "common/model/Notification";
import { MONITORING_USER_NAME } from "common/model/Notification";
import { DESIGNATION_DATE } from "common/model/Notification";
import { DESIGNATION_REPORT } from "common/model/Notification";
import { calculateComponentHeight } from "common/utils/forms";
import { AuditorsContext } from "context/AuditorsContext";
import USER_ID_ENUM from "common/enumerators/UserIdEnum";
import AuditorSimpleInput from "components/auditor/AuditorSimpleInput";
import { manageInputTables } from "common/utils/collections";

export default function DesignationPanel({ data  }) {

  const { getAuditorRegistry } = React.useContext(AuditorsContext);

  return (
    <>
      <Row style={{ marginTop: '5px' }}>
        <Col>
          <AuditorSimpleInput
            label={"Responsável pelo Monitoramento:"}
            user_login={data[DESIGNATION_AUDITOR] ? data[DESIGNATION_AUDITOR] : data[MONITORING_USER_LOGIN]}

            user_registration_id={getAuditorRegistry(data[DESIGNATION_AUDITOR], USER_ID_ENUM.USER_LOGIN).user_registration_id ?
              getAuditorRegistry(data[DESIGNATION_AUDITOR], USER_ID_ENUM.USER_LOGIN).user_registration_id :
              data[MONITORING_USER_REGISTRATION_ID]}

            user_name={getAuditorRegistry(data[DESIGNATION_AUDITOR], USER_ID_ENUM.USER_LOGIN).user_name ?
              getAuditorRegistry(data[DESIGNATION_AUDITOR], USER_ID_ENUM.USER_LOGIN).user_name :
              data[MONITORING_USER_NAME]}

            invalidFlag={false}
            modalReadonlyMode={true}
          />

        </Col>
      </Row >

      <Row style={{ marginTop: '2px' }}>
        <Col>
          <b>Data/Hora da Designação:</b>
          <Input
            type='datetime-local'
            value={data[DESIGNATION_DATE]}
            readOnly
            step="1"
          />
        </Col>
      </Row>

      <Row style={{ marginTop: '5px' }}>
        <Col>
          <b>Observações da Designação:</b>
        </Col>
        <Input
          type='textarea'
          readOnly
          value={manageInputTables(data, [DESIGNATION_REPORT])}
          maxLength="2048"
          style={calculateComponentHeight(manageInputTables(data, [DESIGNATION_REPORT]), "0 14px")}
        />
      </Row>
    </>)
}
