import React from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label
} from "reactstrap";


import { ATF_LINK } from "common/enviroment";
import { DHEMI } from "common/model/Dfe";
import { CNPJ_ISSUER } from "common/model/Dfe";
import { CPF_ISSUER } from "common/model/Dfe";
import { CNPJ_RECIPIENT } from "common/model/Dfe";
import { CPF_RECIPIENT } from "common/model/Dfe";
import { DFE_VALUE } from "common/model/Dfe";
import { FISCAL_NOTIFICATION_TYPE } from "common/model/Notification";
import { DFE_STATUS } from "common/model/Dfe";
import { KEY } from "common/model/Dfe";
import { DFE_TYPE } from "common/model/Dfe";
import { convertUtcToBrazilianDate } from "common/utils/validate";
import { cpfOrCnpjThatsTheQuestion } from "common/utils/validate";
import { getMdfeATFLink } from "common/enviroment";
import { getNfeATFLink } from "common/enviroment";

export default function DfeIssuanceModal({ show, toggle, data }) {
  return (
    <Modal isOpen={show} toggle={toggle} >
      <ModalHeader toggle={toggle}>
        Dados {data.fiscal_notification_type === 2 ? 'da NFe' : 'do MDFe'}
        <br />
        <small style={{ color: "red" }}>
          Necessário estar logado no
          <a
            style={{ color: '#2322c8' }}
            href={ATF_LINK}
            target="_blank"
            rel="noreferrer"
          >
            ATF
          </a>
          para acessar link
        </small>
      </ModalHeader>
      <ModalBody>
        <Form style={{ padding: '1.5rem' }}>
          <FormGroup row>
            <Label style={{ marginRight: '5px' }}><b>Chave {DFE_TYPE[data[FISCAL_NOTIFICATION_TYPE]]}:</b></Label>
            <a
              style={{ color: '#2322c8' }}
              href={
                data[FISCAL_NOTIFICATION_TYPE] === 2 ?
                  getNfeATFLink(data[KEY]) :
                  getMdfeATFLink(data[KEY])
              }
              target="_blank"
              rel="noreferrer"
            >
              {data[KEY]}
            </a>

          </FormGroup>

          <FormGroup row>
            <Label style={{ marginRight: '5px' }}><b>Data e Hora de Emissão: </b></Label>
            {convertUtcToBrazilianDate(data[DHEMI])}
          </FormGroup>

          <FormGroup row>
            <Label style={{ marginRight: '5px' }}><b> CPF/CNPJ Emitente: </b></Label>
            {cpfOrCnpjThatsTheQuestion(data[CPF_ISSUER], data[CNPJ_ISSUER])}
          </FormGroup>

          {data.fiscal_notification_type === 2 &&
            (
              <FormGroup row>
                <Label style={{ marginRight: '5px' }}><b> CPF/CNPJ Destinatário: </b></Label>
                {cpfOrCnpjThatsTheQuestion(data[CPF_RECIPIENT], data[CNPJ_RECIPIENT])}
              </FormGroup>
            )}

          <FormGroup row>
            <Label style={{ marginRight: '5px' }}><b>Valor Total: </b></Label>
            {`R$ ${data[DFE_VALUE].toLocaleString('pt-br', { minimumFractionDigits: 2 })}`}
          </FormGroup>

          <FormGroup row>
            <Label style={{ marginRight: '5px' }}><b>Situação: </b></Label>
            {data[DFE_STATUS]}
          </FormGroup>
        </Form>
      </ModalBody>
    </Modal>
  )
}
