import React from "react";
import {
  Modal,
  ModalBody,
  ModalHeader,
  Spinner
} from 'reactstrap';

import readDfeData from "axios/ReadDfeData";


import useAsync from "common/hooks/useAsync";
import MdfeTable from "components/tables/MdfeTable";
import NfeTable from "components/tables/NfeTable";
import CteTable from "components/tables/CteTable";
import BlankRowTable from "components/tables/BlankRowTable";
import { isEmpty } from "common/utils/collections";
import { ATF_LINK } from "common/enviroment";


export default function DfeModal({ show, toggle, data, fetchData }) {
  const { readDfeDataRequest } = readDfeData()

  const [nfeData, setNfeData] = React.useState(null)
  const [cteData, setCteData] = React.useState(null)
  const [mdfeData, setMdfeData] = React.useState(null)

  const { loading, error, value } = useAsync(async () => {
    let event_id = data["event_id"]
    let res = await readDfeDataRequest(event_id)
    if (!isEmpty(res) && Array.isArray(res)) {
      setMdfeData(res[0])
      setNfeData(res[1])
    }
  }, [fetchData]);

  return (
    <Modal isOpen={show} toggle={toggle} size="xl">
      <ModalHeader toggle={toggle}>
        Lista de DFes
      </ModalHeader>
      <ModalBody>
        {loading ? <Spinner color="info" style={{ width: "4.0rem", height: "4.0rem", margin: '4.0rem' }} /> : ""}
        {error ? <h1 color="info" >Não foi possível recuperar os dados de DFE. </h1> : ""}
        {!isEmpty(value) && (
          <>
            <br/>
            <small style={{color: "red"}}>
              Necessário estar logado no
              <a
              style={{ color: '#2322c8' }}
              href={ATF_LINK}
              target="_blank"
              rel="noreferrer"
              >
              ATF
              </a>
              para acessar link
            </small>
          </>
        )}
        <div className="content">
          {!isEmpty(mdfeData) && <MdfeTable data={mdfeData} headerText="Lista de MDFEs" loading={loading} />}
          {!isEmpty(mdfeData) && !isEmpty(nfeData) && <BlankRowTable margin={20} />}
          {!isEmpty(nfeData) && <NfeTable data={nfeData} headerText="Lista de NFEs" loading={loading} />}
          {!isEmpty(cteData) && <CteTable data={cteData} headerText="Lista de CTEs" loading={loading} />}
        </div>
      </ModalBody>
    </Modal>
  )
}
