import React, { useMemo, useState } from 'react'
import { GoogleMap, Marker } from '@react-google-maps/api'
import { useJsApiLoader } from '@react-google-maps/api';
import { useHistory } from "react-router-dom";
import {
  Card,
  CardBody,
  Row,
  Col,
  Input,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ListGroup, ListGroupItem,
  Spinner, Alert,
  InputGroupText, InputGroup,
} from "reactstrap";
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import axios from "axios";
import axiosRetry from 'axios-retry';
import { getTimeoutConfig } from "axios/AxiosUtils";
import { getRetryConfig } from "axios/AxiosUtils";

import mapOptions from 'common/mapOptions.js'
import env from 'common/enviroment.js'
import USER_ID_ENUM from 'common/enumerators/UserIdEnum'

import Table from 'components/tables/Table'
import Tabs from 'components/Tabs';

import MapLegend from 'components/MapLegend'

import { MapsContext } from 'context/MapsContext'
import { WarningsContext } from 'context/WarningsContext';
import { VehicleContext } from 'context/VehicleContext';
import { AuditorsContext } from 'context/AuditorsContext';
import { WarningsUpdateContext } from "context/WarningsUpdateContext";

import { isLoggedIn } from 'common/SessionUtils';
import { getCortexToken } from 'common/SessionUtils';
import { getUserRole } from 'common/SessionUtils';

import WarningModal from 'components/modals/WarningModal/WarningModal';
import { DESIGNATION_FORM_DATA } from "components/modals/WarningModal/utils/constants";
import { getUserLogin } from 'common/SessionUtils';
import { testAndUnlockWarning } from "common/rules/NotificationRules";
import { isLocked } from 'common/enumerators/LockEnum';
import { isTreated } from 'common/enumerators/NotificationStatus';
import NOTIFICATION_STATUS_ENUM from 'common/enumerators/NotificationStatus';
import { EM_MONITORAMENTO, EM_MONITORAMENTO_LABEL, SEM_COMANDO_FISCAL, SEM_COMANDO_FISCAL_LABEL  } from "common/model/Notification";
import { hasUserLoggedData } from 'common/SessionUtils';
import { getUserRegistrationID } from 'common/SessionUtils';
import { getUserID } from 'common/SessionUtils';
import { getUserName } from 'common/SessionUtils';
import { Notification } from "common/model/Notification";
import { isEmpty } from 'common/utils/collections';
import { getNowDateTimeString } from 'common/utils/validate';
import LocationTrackingModal from 'components/modals/LocationTrackingModal/LocationTrackingModal';
import useToggle from 'common/hooks/useToogle';
import { useModalWithData } from 'common/hooks/useModalWithData';
import MODAL_TYPE_ENUM from 'common/enumerators/ModalTypeEnum';



export default function Maps(props) {

  const body = {
    'cortex_token': getCortexToken(),
    'role': getUserRole()
  }

  const history = useHistory()

  const { setplaca } = React.useContext(VehicleContext)
  const { warnings, traffic, changeVisto, printIsMap, startSSE, wasReloaded, setWasReloaded } = React.useContext(WarningsContext);
  const { getAuditorRegistry } = React.useContext(AuditorsContext);
  const { activeCameras, pointsData, setPointsData, getCamerasData } = React.useContext(MapsContext);
  const { warningsViewed , setWarningsViewed} = React.useContext(WarningsUpdateContext)

  const [spinner, setSpinner] = React.useState(false)

  //const [modalMaps, setModalMaps] = React.useState(false)
  const [tabIndex, setTabIndex] = React.useState(0)
  const [index, setIndex] = useState(0)
  //const toggleMapsModal = () => setModalMaps(!modalMaps)

  const [modalPlaca, setModalPlaca] = React.useState(false)
  const toggleModalPlaca = () => setModalPlaca(!modalPlaca)


  const [warningModalData, setWarningModalData] = React.useState({})
  const [formData, setFormData] = React.useState(DESIGNATION_FORM_DATA)
  const [activeModal, setActiveModal] = React.useState(-1)
  const toggleModal = (i = -1) => {
    setActiveModal(i)
  }
  const [reload, setReload] = React.useState(false)
  const toggleReload = () => setReload(!reload)

  const [fetchDataFlag, toggleFetchData] = useToggle(false)
  const { modalData, setModalData, isModalActive, modalType, toggleModalOn, toggleModalOff } = useModalWithData()

  const [selectedFilter, setSelectedFilter] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const [data, setData] = useState([]);

  const [dropdownOpen, setDropdownOpen] = useState(false);
  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);

  const [isSearchEnabled, setIsSearchEnabled] = useState(false);

  const axiosRetryProxy = axios.create(getTimeoutConfig())
  axiosRetry(axiosRetryProxy, getRetryConfig())

  const { isLoaded: googleMapLoaded, loadError : googleMapError } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: env.googleApiKey
  })


  const [typeCameraFilters, setTypeCameraFilters] = React.useState({
    'DNIT': true,
    'PRF': true,
    'SEMOB': true,
    'Estadual': true
  })

  const filterOptions = [
    {value: 'passagem' , label:'Passagem'},
    {value: 'local', label:'Local'},
    {value: 'uf', label:'UF'},
    {value: 'placa', label:'Placa'},
    {value: 'veiculo', label:'Veículo'},
    {value: 'modelo', label:'Modelo'},
    {value: 'alerta', label:'Alerta'}
  ];

  const displayLegend = {
    "DNIT": "DNIT",
    "EQUIPAMENTOS PRF": "PRF",
    "Pontos Estado": "ESTADO"
    //"SUGESTÕES": "SUGESTÕES"
  }

  const colors = {
    "DNIT": "#0f9d58",
    "EQUIPAMENTOS PRF": "#0288d1",
    "Pontos Estado": "#ffd600"
    //"SUGESTÕES": "#801e0f"
  }
  const categories = Object.keys(colors)
  const [activeMarkers, setActiveMarkers] = React.useState(new Array(categories.length))

  const titleCase = (text) => {
    return text.toLowerCase()
      .split(' ')
      .map((word) => {
        return word[0].toUpperCase() + word.slice(1);
      }).join(' ')
  }
  
  const handleFilterChange = (value) => {
    setSelectedFilter(value);
    setIsSearchEnabled(true);
    console.log(`Filtro selecionado: ${value}`);
  };
  
  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };
  
  const handlePlusButtonClick = () => {
    const filteredData = data.filter(item => {
      if (!selectedFilter) return true;
      return item.nome === selectedFilter;
    }).filter(item => {
      if (!searchTerm) return true;
      return item.nome.toLowerCase().includes(searchTerm.toLowerCase());
    });
  
    setData(filteredData);
  };
  


  const ChangeKey = (nome) => {

    let mudar = []

    nome.split('').forEach((letra, index) => {
      if (nome[index] === `${nome[index].toUpperCase()}`) {
        mudar.push(nome[index])

      }
    })

    mudar.forEach(function (letra) {
      nome = nome.replace(`${letra}`, ` ${letra.toLowerCase()}`)
    })

    return titleCase(nome)

  }

  const pointClick = (index) => {
    return () => {
      let points = [...pointsData]

      points[index] = {
        ...points[index],
        active: !points[index].active
      }

      setPointsData(points)
    }
  }

  const markerClick = (index) => () => {
    let markers = [...activeMarkers]

    markers[index] = !markers[index]

    setActiveMarkers(markers)
  }

  //verificar props do container
  //Aparentemente tem um valor 'auto' para ajustar automaticamente o tamanho da altura para caber
  const containerStyle = {
    width: '100%',
    height: 'auto'
  };

  const setEditionDataOLD = (warning, data) => {

    const auditorData = getAuditorRegistry(getUserLogin(), USER_ID_ENUM.USER_LOGIN);

    if (warning) {

      if (warning['fiscal_action_auditors']) {
        let auditors = {}
        for (var auditorID of warning['fiscal_action_auditors'].split(",")) {
          auditors[auditorID] = getAuditorRegistry(auditorID, USER_ID_ENUM.USER_NAME)
        }
        data["fiscal_action_auditors"] = auditors
      }


      let monitoringAuditor = getAuditorRegistry(warning["fiscal_action_report_auditor"], USER_ID_ENUM.USER_ID)
      if (monitoringAuditor && Object.keys(monitoringAuditor).length > 0) {
        data["monitoring_user_id"] = monitoringAuditor['user_id']
        data["monitoring_user_name"] = monitoringAuditor['user_name']
        data["monitoring_user_registration_id"] = monitoringAuditor['user_registration_id']

      } else {
        data["monitoring_user_name"] = getUserLogin() + ": USUÁRIO NÃO CADASTRADO"

        if (auditorData) {
          data["monitoring_user_registration_id"] = auditorData.user_registration_id
          data["monitoring_user_id"] = auditorData.user_id
          data["monitoring_user_name"] = auditorData.user_name
        }
      }


    }

    return data

  }

  const setEditionData = (warning, data) => {
    

    if (!warning)
      return data

    
    let status = warning.fiscal_notification_status
    data['fiscal_action_designation_report'] = isEmpty(warning['fiscal_action_designation_report']) ? "" :  warning['fiscal_action_designation_report']
    //SE A NOTIFICAÇÃO POSSUI STATUS MAIOR QUE DESIGNADA, TODOS OS CAMPOS OBRIGATORIAMENTE DEVEM ESTAR PREENCHIDOS. 
    if (isTreated(warning) || isLocked(warning) ) {
      
      if (!isEmpty(warning.fiscal_action_auditors)) {
        let homologationAuditor = getAuditorRegistry(warning["fiscal_action_homologation_auditor"], USER_ID_ENUM.USER_LOGIN)
        data["fiscal_action_homologation_auditor_id"] = homologationAuditor['user_registration_id']
      }
      
      let monitoringAuditor = getAuditorRegistry(warning["fiscal_action_designation_auditor"], USER_ID_ENUM.USER_LOGIN)
      data["monitoring_user_id"] = monitoringAuditor['user_id']
      data["monitoring_user_name"] = monitoringAuditor['user_name']
      data["monitoring_user_registration_id"] = monitoringAuditor['user_registration_id']
      data["monitoring_user_login"] = monitoringAuditor['user_login']

      data["fiscal_action_designation_date"] = getNowDateTimeString()
      data["fiscal_action_designation_auditor"] = monitoringAuditor['user_name']
      
      let auditors = {}
      
      //Tratamento para os casos de designação sem comando fiscal. 
      if (status === NOTIFICATION_STATUS_ENUM.EM_MONITORAMENTO || status === NOTIFICATION_STATUS_ENUM.EM_MONITORAMENTO_HOMOLOGADO ) {
        auditors[EM_MONITORAMENTO] = { 'user_name': EM_MONITORAMENTO_LABEL, 'user_login': EM_MONITORAMENTO_LABEL }
      } else if (status === NOTIFICATION_STATUS_ENUM.SEM_COMANDO || status === NOTIFICATION_STATUS_ENUM.SEM_COMANDO_HOMOLOGADA ) {
        auditors[SEM_COMANDO_FISCAL] = { 'user_name': SEM_COMANDO_FISCAL_LABEL, 'user_login': SEM_COMANDO_FISCAL_LABEL }
      } else if (warning['fiscal_action_auditors']) {
        for (var auditorID of warning['fiscal_action_auditors'].split(",")) {
          auditors[auditorID] = getAuditorRegistry(auditorID, USER_ID_ENUM.USER_LOGIN)
        }
      }
      data["fiscal_action_auditors"] = auditors

    } else {
      //SE A NOTIFICAÇÃO POSSUI STATUS MENOR QUE DESIGNADA E O LOCK ESTÁ DESATIVADO, TODOS OS CAMPOS ESTÃO VAZIOS E O USUÁRIO LOGADO PASSARÁ A SER RESPONSÁVEL PELO MONITORAMENTO
      data["monitoring_user_name"] = getUserLogin() + ": USUÁRIO NÃO CADASTRADO"

      if (hasUserLoggedData()) {
        data["monitoring_user_registration_id"] = getUserRegistrationID()
        data["monitoring_user_id"] = getUserID()
        data["monitoring_user_name"] = getUserName()
        data["monitoring_user_login"] = getUserLogin()
      }
    }

    return data
  }

  const openWarningModal = (warning) => {


    let data = { ...DESIGNATION_FORM_DATA, fiscal_action_auditors: {} }
    let notificationObj = new Notification(warning)
    setFormData(setEditionData(warning, data))

    setWarningModalData(notificationObj);

    toggleModal(0);

  }

  const openMapModal = (plate, location) => {
    let modalData = {
      plate: plate,
      location : location

    }
      setModalData(modalData)
      toggleModalOn(MODAL_TYPE_ENUM.MAP, modalData)
      toggleFetchData()
  }

  const Local = (props) => {

    if (props && props.event_json) {

      let placa = props.event_json['evento_infevento_nrplaca']
      let local = props.event_json['evento_infevento_dsreflocal']

      return (
        <>
          <div
            className={props.location !== "null" && "underlined_text"}
            onClick={() => { openMapModal(placa, local) }}
            style={{ cursor: props.location !== "null" && "pointer", color: '#2322c8' }}
          >
            {props.location}
          </div>
        </>
      )
    } else {
      return "-"
    }
  }

  React.useEffect(() => {
    if (isLoggedIn()) {
      printIsMap();
      getCamerasData();
      changeVisto();
      if (wasReloaded) {
        startSSE()

        setWasReloaded(false)
        setWasReloaded(false)
      }
    } else {
      history.push('/login')
    }

    testAndUnlockWarning(warningsViewed, setWarningsViewed)

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  React.useEffect(() => {
    setIndex(0)

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [tabIndex])

  const FilterEvent = (data_row, field_to_filter) => {

    if (data_row[field_to_filter].includes('_LPD')) {

      if (typeCameraFilters.DNIT) {
        return true
      }

      return false
    }

    if (data_row[field_to_filter].startsWith('BR')) {

      if (typeCameraFilters.PRF) {
        return true
      }

      return false
    }

    if (data_row[field_to_filter].startsWith('PB')) {
      if (typeCameraFilters.Estadual) {
        return true
      }

      return false
    }

    if (!typeCameraFilters.SEMOB) {

      return false
    }

    return true
  }
  const createMapModal = useMemo(() => <LocationTrackingModal show={isModalActive}  googleMapOnLoad={googleMapLoaded} googleMapOnError={googleMapError} toggle={toggleModalOff} data={modalData} fetchData={fetchDataFlag} />, [modalData]);

  return (
    <>
      <div className="content">
        <Row>
          <Col md="12">
            {googleMapError && <div>Não foi possível carregar o GoogleMap.</div>}
            {(googleMapLoaded) ? <Card className="card-plain">
                <CardBody>
                  <div
                    id="map"
                    className="map"
                    style={{ position: "relative", overflow: "hidden", display: "flex" }}
                  >
                    <MapLegend categories={categories} colors={colors} activeCategories={activeMarkers} onClick={markerClick} display={displayLegend} />
                    <GoogleMap
                      mapContainerStyle={containerStyle}
                      center={props.center}
                      zoom={props.zoom}
                      options={mapOptions}
                    >
                      {activeCameras.filter(point => {
                        return !activeMarkers[categories.indexOf(point['category'])]
                      }).map((point, index) => (
                        //Marcação passando ícone
                        <Marker
                          className="map-icon"
                          key={`marker-${index}`}
                          position={{ lat: point['nrlatitude'], lng: point['nrlongitude'] }}
                          onClick={pointClick(index)}
                          zIndex={pointsData[index].active ? categories.length + 1 : categories.length - categories.indexOf(point['category'])}
                          options={{
                            icon: {
                              url: `https://ik.imagekit.io/exi1dv80wm/pin/${pointsData[index].active ? '00ff00' : colors[point['category']].slice(-6)}.png`,
                              scaledSize: {
                                height: 26,
                                width: 21
                              }
                            }
                          }}
                        />
                      ))}
                    </GoogleMap>

                  </div>
                </CardBody>
              </Card>
            : ""}

          </Col>
        </Row>

                        
        <Row>
          <Col md="12">
            <Tabs names={['Tabela de alertas', 'Tabela de tráfego']} index={[tabIndex, setTabIndex]} />
            <Card style={{ borderTopLeftRadius: '0px' }}>
              <CardBody>
                <InputGroup>
                  <InputGroupText style={{ backgroundColor: '#51cbce', fontWeight: 'bold', color: 'white', borderColor: '#51cbce', margin: ' 0 30px 0 0', padding: '0 6px' }}>
                    <Input
                      addon
                      aria-label="Checkbox for following text input"
                      type="checkbox"
                      checked={typeCameraFilters.Estadual}
                      style={{ margin: '0 15px 0 0', transform: 'scale(1.3)' }}
                      onChange={event => setTypeCameraFilters({ ...typeCameraFilters, 'Estadual': event.target.checked })}
                    />
                    Estadual
                  </InputGroupText>
                  <InputGroupText style={{ backgroundColor: '#51cbce', fontWeight: 'bold', color: 'white', borderColor: '#51cbce', margin: ' 0 30px 0 0', padding: '0 6px' }}>
                    <Input
                      addon
                      aria-label="Checkbox for following text input"
                      type="checkbox"
                      checked={typeCameraFilters.PRF}
                      style={{ margin: '0 15px 0 0', transform: 'scale(1.3)' }}
                      onChange={event => setTypeCameraFilters({ ...typeCameraFilters, 'PRF': event.target.checked })}
                    />
                    PRF
                  </InputGroupText>
                  <InputGroupText style={{ backgroundColor: '#51cbce', fontWeight: 'bold', color: 'white', borderColor: '#51cbce', margin: ' 0 30px 0 0', padding: '0 6px' }}>
                    <Input
                      addon
                      aria-label="Checkbox for following text input"
                      type="checkbox"
                      checked={typeCameraFilters.DNIT}
                      style={{ margin: '0 15px 0 0', transform: 'scale(1.3)' }}
                      onChange={event => setTypeCameraFilters({ ...typeCameraFilters, 'DNIT': event.target.checked })}
                    />
                    DNIT
                  </InputGroupText>
                  <InputGroupText style={{ backgroundColor: '#51cbce', fontWeight: 'bold', color: 'white', borderColor: '#51cbce', margin: ' 0 30px 0 0', padding: '0 6px' }}>
                    <Input
                      addon
                      aria-label="Checkbox for following text input"
                      type="checkbox"
                      checked={typeCameraFilters.SEMOB}
                      style={{ margin: '0 15px 0 0', transform: 'scale(1.3)' }}
                      onChange={event => setTypeCameraFilters({ ...typeCameraFilters, 'SEMOB': event.target.checked })}
                    />
                    SEMOB
                  </InputGroupText>
                  
                </InputGroup>
                <div className="filters-container">
                  <div className="filter-bar">
                    <div className="filter-options">
                      <Dropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
                        <DropdownToggle caret color="primary">
                          {selectedFilter || 'Selecionar:'}
                        </DropdownToggle>
                        <DropdownMenu>
                          {filterOptions.map((option) => (
                            <DropdownItem key={option.value} onClick={() => handleFilterChange(option.value)}>
                              {option.label}
                            </DropdownItem>
                          ))}
                        </DropdownMenu>
                        <input
                          style={{width: '200px', height: '40px' }}
                          type="text"
                          placeholder="Pesquisar..."
                          value={searchTerm}
                          onChange={handleSearchChange}
                          disabled={!isSearchEnabled} 
                        />
                        <button style={{ backgroundColor: '#51cbce', color: 'white', border: 'none', padding: '5px 10px', cursor: 'pointer', fontWeight: 'bold', marginLeft: '10px' ,width: '100px', height: '40px' }} on click={handlePlusButtonClick}>
                          +
                        </button>

                      </Dropdown>
                      </div>
                      <TabelaDados dados = {data}/>
                    </div>
                </div>
                <Card>
                  <CardBody>
                    {tabIndex === 0 &&
                      <Table
                        state={[index, setIndex]}
                        data={warnings.filter(warning => FilterEvent(warning, 'evento_infevento_dsreflocal'))}
                        invalid={["NULL", "null", undefined, null, "", "-", "0"]}
                        pageSize={10}
                        columns={{
                          "Alerta": {
                            Func: (props) =>
                              <p                                
                              >
                                {props.data}
                              </p>,
                            args: "nome"
                          },
                          "Passagem": {
                            Func: (props) => {
                              const dateObj = new Date(props.data).toLocaleString("pt-BR").split(' ')

                              return <div style={{ textAlign: 'center', maxWidth: 80 }}>{dateObj[0]}<br />{dateObj[1]}</div>
                            },
                            args: "evento_infevento_dhregpassagem"
                          },
                          "Local": {
                            Func: (props) => {
                              return (
                                <Local location={props.data} event_json={props.row_data} />
                              )
                            },
                            args: "evento_infevento_dsreflocal"
                          },
                          "UF": "evento_infevento_sguf",
                          "Placa": {
                            Func: (props) =>
                              <p
                                className='links-table'
                                onClick={() => { setModalData(props.row_data); toggleModalPlaca() }}
                              >
                                {props.data}
                              </p>,
                            args: "evento_infevento_nrplaca",
                            headerStyle: { textAlign: "center" }
                          },
                          "Veículo": "evento_infevento_dstipoveiculo",
                          "Modelo": "evento_infevento_nomarcamodelo"
                        }}
                      />
                    }
                    {tabIndex === 1 &&
                      <Table
                        state={[index, setIndex]}
                        data={traffic.filter(event => FilterEvent(event, 'dsreflocal'))}
                        pageSize={100}
                        invalid={["NULL", "null", undefined, null, "", "-", "0"]}
                        style={{ fontSize: '0.8rem' }}
                        columns={{
                          "Passagem": {
                            Func: (props) => {
                              const dateObj = new Date(props.data)
                              const dateHour = dateObj.toLocaleString("pt-BR").split(' ')

                              return <div style={{ textAlign: 'center', maxWidth: 80 }}>{dateHour[0]}<br />{dateHour[1]}</div>
                            },
                            args: "dhregpassagem"
                          },
                          "Local": {
                            Func: (props) => <Local location={props.data} event_json={props.row_data} />,
                            args: "dsreflocal"
                          },
                          "UF": "sguf",
                          "Placa": {
                            Func: (props) => {
                              const [modal, setModal] = React.useState(false);
                              const [dataVehicle, setdataVehicle] = React.useState([])
                              const [isErroInPost, setisErroInPost] = React.useState(false)

                              const ToVehicleRoute = () => {
                                setplaca(props.data)
                              }

                              const closeModalVehicle = () => {
                                setModal(false)
                              }

                              function sleep(time) {
                                return new Promise((resolve) => setTimeout(resolve, time));
                              }

                              const toggleModal = () => {
                                setisErroInPost(false)
                                axiosRetryProxy.post(`${env.apiAddress}/cortex/license/${props.data}`, body).then(res => {
                                  let data_consulta = []
                                  sleep(2000).then(() => {
                                    // Do something after the sleep!
                                    Object.entries(res.data).forEach(([key, value]) => {

                                      if (key === "placa" || key === "marcaModelo" || key === "anoFabricacao" || key === "anoModelo"
                                        || key === "cor" || key === "categoria" || key === "especie" || key === "tipoVeiculo" || key === "ufEmplacamento"
                                        || key === "municipioPlaca") {
                                        data_consulta.push({
                                          "chave": key ? ChangeKey(key) : '',
                                          "valor": value ? value : ''
                                        })
                                      }
                                    })
                                    setdataVehicle(data_consulta)
                                  });

                                }).catch((err) => {
                                  sleep(1000).then(() => {
                                    setisErroInPost(true)
                                  });

                                }).finally(() => {
                                  sleep(1000).then(() => {
                                    setModal(true);
                                  });

                                })
                              }

                              return (
                                <>
                                  <div
                                    className="underlined_text"
                                    onClick={toggleModal}
                                    style={{ textAlign: 'center', cursor: "pointer" }}
                                  >
                                    {props.data}
                                  </div>

                                  <Modal isOpen={modal} toggle={toggleModal}>
                                    <ModalHeader toggle={closeModalVehicle}>{`Placa ${props.data}`}</ModalHeader>
                                    <ModalBody>
                                      <ListGroup>
                                        {
                                          (dataVehicle.length && Array.isArray(dataVehicle)) ?
                                            dataVehicle.map((dados, index) => (
                                              <ListGroupItem key={index} className="justify-content-between">
                                                <Row>
                                                  <Col>
                                                    <b>{dados.chave}</b>
                                                  </Col>
                                                  <Col>
                                                    {dados.valor}
                                                  </Col>
                                                </Row>
                                              </ListGroupItem>
                                            ))
                                            :
                                            isErroInPost ?
                                              <Row className="text-center">
                                                <Col>
                                                  <Alert color="danger">
                                                    Error !
                                                  </Alert>
                                                </Col>
                                              </Row>
                                              :
                                              <Row Row className="text-center">
                                                <Col>
                                                  <Spinner color="info" />
                                                </Col>
                                              </Row>
                                        }
                                      </ListGroup>
                                      {Array.isArray(dataVehicle) && dataVehicle.length > 0 &&
                                        <Row>
                                          <hr></hr>
                                          <Button color="primary" className="btn-md" onClick={ToVehicleRoute}>
                                            Mais Informações
                                          </Button>
                                        </Row>
                                      }
                                    </ModalBody>
                                  </Modal>
                                </>
                              )
                            },
                            args: "nrplaca",
                            headerStyle: { textAlign: "center" }
                          },
                          "Veículo": "dstipoveiculo",
                          "Modelo": "nomarcamodelo"
                        }}
                      />
                    }
                    {activeModal === 0 &&
                      <WarningModal
                        show={true} data={warningModalData} formData={formData} editionMode={false}
                        setFormData={setFormData} toggle={() => toggleModal(-1)} reloadMethod={toggleReload} spinner={spinner}
                      />
                    }
                    {
                      <Modal isOpen={modalPlaca} toggle={toggleModalPlaca}>
                        <ModalHeader toggle={toggleModalPlaca}>{`Placa ${modalData['nrplaca']}}`}</ModalHeader>
                        <ModalBody style={{ justifyContent: 'center', alignContent: 'center', display: 'flex' }}>
                          {/*<img alt="Foto da placa" src="https://i.ytimg.com/vi/lmVlZbmNgGw/hqdefault.jpg" />*/}
                        </ModalBody>
                      </Modal>
                    }
                  </CardBody>
                </Card>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
      {modalType === MODAL_TYPE_ENUM.MAP && createMapModal}
    </>
  );
}

Maps.defaultProps = {
  center: {
    lat: -7.254653852199306,
    lng: -36.69661154766497
  },
  zoom: 4
}
