const env = {
    apiAddress: process.env.API_ADDRESS || "http://sefaz-liepe.dev.lavid.ufpb.br:5000",    
    hasuraAddress: process.env.HASURA_ADDRESS || "https://sefaz-liepe.dev.lavid.ufpb.br/v1/graphql",
    googleApiKey: process.env.GOOGLE_API_KEY || "AIzaSyDehMFYR2J2BsSsIe3cphH6SJSWyawc7gc",
    citiesPbApi: process.env.CITIES_PB_URL || "https://servicodados.ibge.gov.br/api/v1/localidades/estados/PB/municipios"
}

//TODO trocar este valor literal por uma uma leitura de uma variável de ambiente
export const EDITION_TIMEOUT = 1000000000

export const READ_TIMEOUT = 1000000000

export const LOGIN_TIMEOUT = 1000000000

export const ATF_LINK = `https://ap1pro.sefaz.pb.gov.br/atf/`

export const getMdfeATFLink = (mdfe_key) => {
    return `https://ap1pro.sefaz.pb.gov.br/atf/fis/FISf_ExibirMDFeCompleto.do?edtID=${mdfe_key}&hidAcao=detalhar`
}

export const getNfeATFLink = (nfe_key) => {
    return `https://ap1pro.sefaz.pb.gov.br/atf/fis/FISf_ExibirNFeCompleta.do?edtID=${nfe_key}&hidAcao=consultar`
}

export const getCteATFLink = (cte_key) => {
    return `https://ap1pro.sefaz.pb.gov.br/atf/fis/FISf_ExibirCTeCompleta.do?edtID=${cte_key}&hidAcao=consultar`
}


export default env
