import React from "react";
import {
  Modal, ModalBody, ModalHeader,
  Row
} from "reactstrap";
import Table from "components/tables/Table";
import { NAME } from "common/model/WarningConfig";
import { CLOSING_DATE } from "common/model/WarningConfig";
import { convertUtcToBrazilianDate } from "common/utils/validate";
import { trimText } from "common/utils/forms";
import { mountClosingDateText } from "common/utils/forms";
import { SEVERITY } from "common/model/WarningConfig";
import { DESCRIPTION } from "common/model/WarningConfig";
import Severity from "components/table-elements/Severity";


export default function NearClosingWarningModal({ show, toggle, data  }) {
  return (
    <>
    <Modal isOpen={show} toggle={toggle} size="lg" >
          <ModalHeader toggle={toggle} style={{ padding: "10px 15px" }}>
            Alertas com data de fechamento próximo.
          </ModalHeader>
          <ModalBody style={{ alignContent: 'center', paddingTop: "4px" }}>
            {Array.isArray(data) ?
              <Row style={{ margin: '0 0 20px 0' }}>
                <Table >
                  <thead className="text-primary">
                    <tr>
                      <th>Prioridade</th>
                      <th>Alerta</th>
                      <th>Descrição</th>
                      <th>Data de Fechamento</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      data.map((warning, index) => {
                        return (
                          <tr key={index} >
                            <td><Severity severity={warning[SEVERITY]} /></td>
                            <td style={{ wordBreak: 'break-word' }}>{warning[NAME]}</td>
                            <td style={{ wordBreak: 'break-word' }}>{trimText(warning[DESCRIPTION])}</td>
                            <td>{convertUtcToBrazilianDate(warning[CLOSING_DATE])}<div><b>{mountClosingDateText(warning[CLOSING_DATE])}</b></div></td>
                          </tr>
                        )
                      })
                    }
                  </tbody>
                </Table>
              </Row>

              : <div></div>
            }
          </ModalBody>
        </Modal>
    </>
  )
}
