import { CHAR } from "axios/AxiosUtils"
import { IS_NULL } from "axios/AxiosUtils"
import { VARCHAR } from "axios/AxiosUtils"
import { EVENT_VEHICLE_TYPE } from "common/model/Event"
import { EVENT_LOCAL } from "common/model/Event"
import { EVENT_VEHICLE_MODEL } from "common/model/Event"
import { EVENT_VEHICLE_PLATE } from "common/model/Event"
import { EVENT_VEHICLE_COLOUR } from "common/model/Event"
import { EVENT_CREATION_TIMESTAMP } from "common/model/Event"
import { AWARENESS_AUDITOR } from "common/model/Notification"
import { SUPERVISION_DATE } from "common/model/Notification"
import { FISCAL_NOTIFICATION_STATUS } from "common/model/Notification"
import { AWARENESS_DATE } from "common/model/Notification"
import { HOMOLOGATION_AUDITOR } from "common/model/Notification"
import { SUPERVISION_AUDITOR } from "common/model/Notification"
import { ID_CONFIG } from "common/model/Notification"
import { FISCAL_ACTION_DATE } from "common/model/Notification"
import { FISCAL_ACTION_AUDITORS } from "common/model/Notification"
import { DESIGNATION_AUDITOR } from "common/model/Notification"
import { FISCAL_ACTION_REPORT } from "common/model/Notification"
import { ID } from "common/model/Notification"
import { CREATOR } from "common/model/WarningConfig"
import { NAME } from "common/model/WarningConfig"
import { integerFilter, warningStatusFilter, dateFilter} from "components/tags/filterPresets"


export const TAGS_INPUT_FILTERS = {
    "Nº Ocorrência": {
      field: ID,
      size: 100,
      ...integerFilter
    },
    "Nº Alerta": {
      field: ID_CONFIG,
      size: 10,
      ...integerFilter
    },
    "Alerta": {
      field: NAME,
      column_type: VARCHAR
    },
    "Data/Hora": {
      field: EVENT_CREATION_TIMESTAMP,
      ...dateFilter
    },
    "Local": {
      field: EVENT_LOCAL,
      column_type: VARCHAR,
      size: 120
    },
  
    "Veículo": {
      field: EVENT_VEHICLE_TYPE,
      column_type: VARCHAR,
      size: 20
    },
    "Modelo": {
      field: EVENT_VEHICLE_MODEL,
      column_type: VARCHAR,
      size: 40
    },
    "Cor": {
      field: EVENT_VEHICLE_COLOUR,
      column_type: VARCHAR,
      size: 20,
      only_letters_filter_input: true
    },
    "Placa": {
      field: EVENT_VEHICLE_PLATE,
      column_type: VARCHAR,
      size: 8,
      only_letters_and_numbers: true
    },
    "Ação Fiscal": {
      field: "procedimentos",
      column_type: VARCHAR
    },
    "Ação Realizada": {
      field: FISCAL_ACTION_REPORT,
      column_type: VARCHAR
    },
    "Data/Hora da Ação": {
      field: FISCAL_ACTION_DATE,
      ...dateFilter
    },
    "Responsável pela Ação Fiscal": {
      field: FISCAL_ACTION_AUDITORS,
      column_type: VARCHAR,
      only_letters_filter_input: true
    },
    "Responsável pelo Monitoramento": {
      field: DESIGNATION_AUDITOR,
      column_type: VARCHAR,
      only_letters_filter_input: true
    },
    "Responsável pela Supervisão": {
      field: SUPERVISION_AUDITOR,
      column_type: VARCHAR,
      only_letters_filter_input: true
    },
    "Responsável pela Homologação": {
      field: HOMOLOGATION_AUDITOR,
      column_type: VARCHAR,
      only_letters_filter_input: true
    },
    "Criador": {
      field: CREATOR,
      column_type: VARCHAR,
      adminOnly: true
    },
    "Ciência": {
      dropdown: {
        "CIÊNCIA PENDENTE": () => {
          return [{
            column: AWARENESS_AUDITOR,
            column_type: CHAR,
            filter_type: IS_NULL,
            value: "false"
          },
          {
            column: AWARENESS_DATE,
            column_type: CHAR,
            filter_type: IS_NULL,
            value: "true"
          }]
        },
        "CIÊNCIA DECLARADA": () => {
          return [{
            column: AWARENESS_AUDITOR,
            column_type: CHAR,
            filter_type: IS_NULL,
            value: "false"
          },
          {
            column: AWARENESS_DATE,
            column_type: CHAR,
            filter_type: IS_NULL,
            value: "false"
          },
          ]
        },
      },
      inputType: "select"
    },
    "Supervisão": {
      dropdown: {
        "SUPERVISÃO PENDENTE": () => {
          return [{
            column: SUPERVISION_AUDITOR,
            column_type: CHAR,
            filter_type: IS_NULL,
            value: "false"
          },
          {
            column: SUPERVISION_DATE,
            column_type: CHAR,
            filter_type: IS_NULL,
            value: "true"
          }]
        },
        "SUPERVISÃO DECLARADA": () => {
          return [{
            column: SUPERVISION_AUDITOR,
            column_type: CHAR,
            filter_type: IS_NULL,
            value: "false"
          },
          {
            column: SUPERVISION_DATE,
            column_type: CHAR,
            filter_type: IS_NULL,
            value: "false"
          },
          ]
        },
      },
      inputType: "select"
    },
    "Situação": {
      field: FISCAL_NOTIFICATION_STATUS,
      ...warningStatusFilter
    },
  
  }
