import React from "react";
import { Row, Col, Table} from "reactstrap";

import MessageModal from "components/modals/MessageModal";

import { EDITION, ERROR } from "components/modals/ModalConsts";

import { WarningsContext } from "context/WarningsContext";
//import { RiInstagramFill, RiLinkedinBoxFill, RiComputerLine, RiHeartFill } from 'react-icons/ri'


require('dotenv').config()
console.log(process.env)
// styles
export default function Footer() {

  const {deadServices} = React.useContext(WarningsContext)

  const [modalSettings, setModalSetting] = React.useState({
    data: {
      operation: EDITION,
      messageType: ERROR,
      message: " ",
    },
    show: false
  });

  const toggleMessageModal = () => {
    setModalSetting((prev) => ({
      ...prev,
      show: false
    }));
  };

  const DeadServicesTable = () => {

    return (
      <>
        Serviços indisponíveis
        <Table >
          <thead className="text-primary">
            <tr>
              <th>Serviço</th>
            </tr>
          </thead>
          <tbody>
            {
              deadServices.map((service, index) => {
                return (
                  <tr key={index} >
                    <td>{service}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </Table>
      </>
    );
  };

  //`Serviços indisponíveis: ${deadServices.join(' ,')}`

  const showDeadServices = () => {
    if (deadServices.length > 0) {
      setModalSetting((prev) => ({
        ...prev,
        data: {...prev.data, message: <DeadServicesTable/>},
        show: true
      }));
    }
  };

  React.useEffect(() => {

    showDeadServices()

  }, [deadServices])

  function buildStr() {
    let data = process.env.REACT_APP_GIT_DATE;
    let split = data.split("T");
    data = split[0] + " - " + split[1];

    let str = 'Última atualização em: ';
    str += data;
    str += '. Commit: ';
    str += process.env.REACT_APP_GIT_SHA;
    str += '. Branch: ';
    str += process.env.REACT_APP_GIT_BRANCH;

    return str;
  }

  return (
    <footer className="footer">
      <MessageModal {...modalSettings} toggle={toggleMessageModal}/>
      <hr />

      <Row className="links">
        <Col>
          <h5 style={{textAlign: 'center', fontSize: '15px'}}>{buildStr()}</h5>
        </Col>
      </Row>
    </footer>
  );
}
