import React from "react";

import {
  Card,
  CardBody,
  Row,
  Col,
} from "reactstrap";
import { useHistory } from "react-router-dom";


import WarningModal from "components/modals/WarningModal/WarningModal";
import MODAL_TYPE_ENUM from "common/enumerators/ModalTypeEnum";

import { GroupsContext } from "context/GroupsContext";
import { AuditorsContext } from "context/AuditorsContext";
import { NavigationContext } from "context/NavigationContext";
import { WarningsContext } from "context/WarningsContext";
import { isLoggedIn, reloadVisibilityGroups } from "common/SessionUtils";
import { ViewsContext } from "context/ViewsContext";
import { Notification } from "common/model/Notification";
import { DESIGNATION_FORM_DATA } from "components/modals/WarningModal/utils/constants";

import useNotification from "common/hooks/useNotification";
import { useModalWithData } from "common/hooks/useModalWithData";
import Tabs from "components/Tabs";
import MdfeWarningTable from "./tables/MdfeWarningTable";
import NfeWarningTable from "./tables/NfeWarningTable";
import useAsync from "common/hooks/useAsync";
import LoadingPage from "layouts/Loading";
import { delay } from "common/utils/forms";
import { WarningsUpdateContext } from "context/WarningsUpdateContext";
import { isEmpty } from "common/utils/collections";




export default function DfeIssuance() {
  const history = useHistory()
  const { refresh} = React.useContext(ViewsContext)
  const { fetchGroupsData, groupsDataMap, getMonitoringGroupOwner, getGroupOwnerData, getGroupSupervisorData } = React.useContext(GroupsContext);
  const { groupsSimpleMap, fetchGroupsName } = React.useContext(GroupsContext);
  const { startWarningsViewedSSE } = React.useContext(WarningsUpdateContext)
  const { auditorsData, getAuditorsData } = React.useContext(AuditorsContext);
  const { setIsMap } = React.useContext(NavigationContext);
  const { startSSE, wasReloaded, setWasReloaded } = React.useContext(WarningsContext);
  const { getAuditorRegistry } = React.useContext(AuditorsContext);
  const { modalData, setModalData, isModalActive, modalType, toggleModalOn, toggleModalOff } = useModalWithData()
  const { mountEditionData } = useNotification(getAuditorRegistry, getMonitoringGroupOwner, getGroupOwnerData, getGroupSupervisorData)

  const [tabIndex, setTabIndex] = React.useState(0)
  const [formData, setFormData] = React.useState(DESIGNATION_FORM_DATA)
  const [intervalId, setIntervalId] = React.useState(false)

  const tabs = ['Alertas de Emiss達o de MDFE', 'Alertas de Emiss達o de NFE']

  const { loading } = useAsync(async () => {
    if (wasReloaded && isLoggedIn()) {
      setIsMap(false);
      reloadVisibilityGroups()

      if (isEmpty(groupsDataMap)) {
        await fetchGroupsData()
      }

      if (isEmpty(groupsSimpleMap)) {
        await fetchGroupsName()
      }

      if (isEmpty(auditorsData)) {
        await getAuditorsData();
      }

      startWarningsViewedSSE()
      startSSE()

      await delay(2000);
      setWasReloaded(false)
    }
  }, [wasReloaded]);




  React.useEffect(() => {
    if (isLoggedIn()) {

      if (intervalId)
        clearInterval(intervalId)

      setIntervalId(setInterval(() => { refresh() }, (5 * 60000), 0, true))

    } else {
      history.push('/login')
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])



  const openWarningModal = (warning) => {
    let notificationObj = new Notification(warning)
    setFormData(mountEditionData(notificationObj, ""))
    setModalData(() => notificationObj);
    toggleModalOn(MODAL_TYPE_ENUM.WARNING)

  }

  if (loading && wasReloaded) {
    return <LoadingPage />
  } else {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="12">
              <Tabs names={tabs} index={[tabIndex, setTabIndex]} />
              <Card>
                <CardBody>
                  {tabIndex === 0 &&
                    <MdfeWarningTable openWarningModal={openWarningModal} />
                  }

                  {tabIndex === 1 &&
                    <NfeWarningTable openWarningModal={openWarningModal} />
                  }

                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>
        {modalType === MODAL_TYPE_ENUM.WARNING && <WarningModal show={isModalActive} toggle={toggleModalOff} data={modalData} formData={formData} setFormData={setFormData} reloadMethod={refresh} tableView={false} />}
      </>
    );
  }
}
