import React from "react";
import { ViewsContext } from "context/ViewsContext";
import TagsInput from "components/tags/TagsInput";
import Table from "components/tables/Table";
import MODAL_TYPE_ENUM from "common/enumerators/ModalTypeEnum";
import { useModalWithData } from "common/hooks/useModalWithData";
import DfeIssuanceModal from "components/modals/DfeIssuanceModal/DfeIssueanceModal";
import NOTIFICATION_TYPE_ENUM from "common/enumerators/NotificationTypeEnum";
import { createBaseHttpRequestBody, createQueryHttpRequestBody } from "axios/AxiosUtils";
import { isExternalUser } from "common/SessionUtils";
import { formatCnpjCpf } from "common/utils/validate";
import { SEVERITY } from "common/model/WarningConfig";
import Severity from "components/table-elements/Severity";
import { ID_CONFIG } from "common/model/WarningConfig";
import { NAME } from "common/model/WarningConfig";
import Warning from "components/table-elements/Warning";
import { DHEMI } from "common/model/Dfe";
import { FISCAL_NOTIFICATION_DATE } from "common/model/Notification";
import { CNPJ_ISSUER } from "common/model/Dfe";
import { CPF_ISSUER } from "common/model/Dfe";
import { CNPJ_RECIPIENT } from "common/model/Dfe";
import { CPF_RECIPIENT } from "common/model/Dfe";
import { DFE_VALUE } from "common/model/Dfe";
import { FISCAL_NOTIFICATION_TYPE } from "common/model/Notification";
import { DFE_TYPE } from "common/model/Dfe";
import { mountGroupsFilterClause } from "axios/AxiosUtils";
import { GROUP_IDS } from "axios/AxiosUtils";
import { QUERY } from "axios/AxiosUtils";
import { FILTERS } from "axios/AxiosUtils";
import { dfeIssuanceTagFilters } from "../utils/filters";
import { DfeTimestamp } from "../utils/format-data";


export default function NfeWarningTable({openWarningModal }) {

  const { refreshFlag, nfeIssuanceFilters, setNfeIssuanceFilters } = React.useContext(ViewsContext)
  const [isLoading, setIsLoading] = React.useState(false)
  const [filters, setFilters] = React.useState([])

  const { modalData, setModalData, isModalActive, modalType, toggleModalOn, toggleModalOff } = useModalWithData()


  const createReadHttpRequestBody = () => {
    let httpRequestBody = createQueryHttpRequestBody(filters)
    let groups = mountGroupsFilterClause(nfeIssuanceFilters)

    httpRequestBody[QUERY][GROUP_IDS] = groups
    httpRequestBody[FISCAL_NOTIFICATION_TYPE] = NOTIFICATION_TYPE_ENUM.NFE

    return httpRequestBody
  }

  const createCountHttpRequestBody = () => {

    let httpRequestBody = createBaseHttpRequestBody()
    let groups = mountGroupsFilterClause(nfeIssuanceFilters)

    httpRequestBody[GROUP_IDS] = groups
    httpRequestBody[FILTERS] = filters
    httpRequestBody[FISCAL_NOTIFICATION_TYPE] = NOTIFICATION_TYPE_ENUM.NFE

    return httpRequestBody
  }


  return (<>
     <TagsInput
              tagsState={[nfeIssuanceFilters, setNfeIssuanceFilters]}
              setQuery={setFilters}
              columns={dfeIssuanceTagFilters}
            />
            <Table
              dataUrl="/warnings/read_all"
              countUrl="/warnings/count_warnings"
              timeout={10000}
              fillData
              tags={filters}
              loading={[isLoading, setIsLoading]}
              refresh={refreshFlag}
              requestBody={createReadHttpRequestBody}
              countRequestBody={createCountHttpRequestBody}
              columns={{
                "Prioridade": {
                  args: SEVERITY,
                  thStyle: { textAlign: 'center' },
                  func: (severity) =>  <Severity severity={severity} />
                },
                "Nº Alerta": {
                  args: ID_CONFIG,
                  thStyle: { textAlign: 'right' },
                  tdStyle: { textAlign: 'right' },
                },
                "Alerta": {
                  args: NAME,
                  thStyle: { textAlign: 'center' },
                  tdStyle: { whiteSpace: 'pre', textAlign: 'center' },
                  Func: ({ row_data }) => <Warning data={row_data} toggleModal={openWarningModal}/>
                },
                "Data/Hora Emissão": {
                  args: DHEMI,
                  thStyle: { textAlign: 'center' },
                  tdStyle: { whiteSpace: 'pre', textAlign: 'center' },
                  func: (date) => <DfeTimestamp date={date}/>
                },
                "Disparo": {
                  args: FISCAL_NOTIFICATION_DATE,
                  thStyle: { textAlign: 'center' },
                  tdStyle: { whiteSpace: 'pre', textAlign: 'center' },
                  func: (date) => <DfeTimestamp date={date}/>
                },
                "Emitente": {
                  args: [CNPJ_ISSUER, CPF_ISSUER],
                  thStyle: { textAlign: 'right' },
                  tdStyle: { textAlign: 'right' },
                  func: (emitente) => emitente[0] ? formatCnpjCpf(emitente[0]) : formatCnpjCpf(emitente[1])
                },
                "Destinatário": {
                  args: [CNPJ_RECIPIENT, CPF_RECIPIENT],
                  thStyle: { textAlign: 'right' },
                  tdStyle: { textAlign: 'right' },
                  func: (destinatario) => destinatario[0] ? formatCnpjCpf(destinatario[0]) : formatCnpjCpf(destinatario[1])
                },
                "Valor": {
                  args: DFE_VALUE,
                  thStyle: { textAlign: 'right' },
                  tdStyle: { textAlign: 'right' },
                  func: (valor) => { return (valor) ? ` R$ ${valor.toLocaleString('pt-br', { minimumFractionDigits: 2 })}` : "" }
                },
                "DFE": {
                  args: FISCAL_NOTIFICATION_TYPE,
                  Func: (props) => {
                    return (isExternalUser()) ? "" : <div
                      className={props.type !== null && "underlined_text"}
                      onClick={() => { setModalData(props.row_data); toggleModalOn(MODAL_TYPE_ENUM.DFE_ISSUANCE) }}
                      style={{ textAlign: 'center', cursor: props.type !== null && "pointer", color: '#2322c8' }}
                      >
                        {DFE_TYPE[props.data]}
                      </div>
                  }
                }
              }} 
      />
 
      {modalType === MODAL_TYPE_ENUM.DFE_ISSUANCE &&  <DfeIssuanceModal show={isModalActive} toggle={toggleModalOff} data={modalData} />}
      
  </>)
}
