import React from "react"; import {
    Modal,
    ModalBody,
    ModalHeader,
} from 'reactstrap';

import { GoogleMap, Marker } from "@react-google-maps/api";

import mapOptions from 'common/mapOptions.js'

const containerStyle = {
    width: 'auto',
    height: '600px'
};

export default function SimpleMapModal({ show, toggle, data, googleMapLoaded, googleMapError }) {
    let warning = data['warning']
    return <Modal isOpen={show} toggle={toggle} style={{ maxWidth: '80%' }}>
        <ModalHeader toggle={toggle}>
            {warning['evento_infevento_dsreflocal']}
        </ModalHeader>
        <ModalBody>
        {googleMapError && <div>Não foi possível carregar o GoogleMap.</div>}
        {(googleMapLoaded) ? <GoogleMap
                mapContainerStyle={containerStyle}
                center={{
                    lat: warning['evento_infevento_nrlatitude'],
                    lng: warning['evento_infevento_nrlongitude']
                }}
                zoom={11}
                options={mapOptions}
            >
                <Marker
                    position={{
                        lat: warning['evento_infevento_nrlatitude'],
                        lng: warning['evento_infevento_nrlongitude']
                    }}
                >

                </Marker>
            </GoogleMap> : ""}
        </ModalBody>
    </Modal>


}
