export function NumericFilter(event, size=undefined){

    var value =  event.target.value;
    if (size && !(value.length < size)){
      event.preventDefault()
    } else if ((event.charCode >= 48 && event.charCode <= 57)  || event.charCode >= 192){
      event.preventDefault()
    }
  
  }
  
  export function LetterFilter(event, size=undefined){
    var value =  event.target.value;
    if (size && !(value.length < size)){
      event.preventDefault()
    } else if (!((event.charCode >= 48 && event.charCode <= 57) || event.charCode === 44) ){
      event.preventDefault()
    }
  }
  
  export function NegativeValuesFilter(event, size=undefined){
    var value =  event.target.value;
    if (size && !(value.length < size)){
      event.preventDefault()
    } else if (!((event.charCode >= 48 && event.charCode <= 57) || event.charCode === 44 || event.charCode === 45) ){
      event.preventDefault()
    }
  }
  
  export function AccentFilter(event, size=undefined){
    var value =  event.target.value;
    if (size && !(value.length < size)){
      event.preventDefault()
    } else if (event.charCode >= 192){
      event.preventDefault()
    }
  }


  export function onlyLettersAndNumbers(event, size=undefined){
    var value =  event.target.value;
    if (size && !(value.length < size)){
      event.preventDefault()
    } else if (!event.key.match(/[a-zA-Z0-9]/)) {
      event.preventDefault();
    }
  }
