import React from "react";

import {
  Row,
  Input,
  Col,
  Label,
} from 'reactstrap';


import { REPORT_ENABLED } from "common/model/Notification";
import { REPORT_AI } from "common/model/Notification";
import { REPORT_DAR } from "common/model/Notification";
import { REPORT_REVENUE_CODE } from "common/model/Notification";
import { REPORT_PENALTY_VALUE } from "common/model/Notification";
import { TRAILER_PLATE } from "common/model/Notification";
import { REPORT_CPF_DRIVER } from "common/model/Notification";
import { REPORT_BOOKED_CPF } from "common/model/Notification";
import { REPORT_BOOKED_CNPJ } from "common/model/Notification";
import { REPORT_BOOKED_IE } from "common/model/Notification";
import { REPORT_LATITUDE } from "common/model/Notification";
import { REPORT_LONGITUDE } from "common/model/Notification";
import { REPORT_PRODUCT_NAME } from "common/model/Notification";
import { validateData, aiInput } from "../utils/rules";
import { LetterFilter } from "common/utils/filters";
import { EVENT_VEHICLE_PLATE } from "common/model/Event";
import { AccentFilter } from "common/utils/filters";
import { NegativeValuesFilter } from "common/utils/filters";
import { isEmpty } from "common/utils/collections";
import { treatNullValue } from "common/utils/collections";
import { cpfInput, cnpjInput } from "components/tags/filterPresets";

export default function ReportPanel({ data, setData, oldData, readOnly, invalid }) {

  const [errorMessage, setErrorMessage] = React.useState(false)

  
  const isDetailedReportDisabled = (readOnly) => {

    if (readOnly === true) {
      return readOnly
    }

    return !data[REPORT_ENABLED]
  }

  const handleCheckboxInput = () => {
    let newFormData = { ...data, report_enabled: !data[REPORT_ENABLED] }
    if (data[REPORT_ENABLED]) {
      //se o checkbox está ativado, quer dizer que o novo estado é que não é necessário preencher o relatório de autuação.
      //sendo assim, é necessário apagar os dados que tenham sido preenchidos. 
      newFormData.report_ai = ''
      newFormData.report_dar = ''
      newFormData.report_revenue_code = ''
      newFormData.report_penalty_value = 0
      newFormData.vehicle_plate = ''
      newFormData.trailer_plate = ''
      newFormData.report_cpf_driver = ''
      newFormData.report_booked_ie = ''
      newFormData.report_booked_cpf = ''
      newFormData.report_booked_cnpj = ''
      newFormData.report_latitude = 0
      newFormData.report_longitude = 0
      newFormData.report_product_name = ''
      newFormData.fiscal_action_homologation_auditor = ''
    }
    setData(newFormData)
  }


  const validateInput = () => {
    let result = validateData(data)
    setErrorMessage(result)
  }


  return (
    <>

      <Row style={{ marginTop: '5px' }}>
        <Col>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <b>Abordagem Realizada:</b>
            <Input
              type='checkbox'
              checked={data[REPORT_ENABLED]}
              readOnly={readOnly}
              disabled={readOnly}
              style={{ marginLeft: "10px", position: 'relative', bottom: '1.5px' }}
              onChange={e => { handleCheckboxInput() }}
            //onChange={e => {setData({ ...data, report_enabled: e.target.checked }); cleanReportInputs(e.target.checked)}}
            />
          </div>
        </Col>
      </Row>
      <Row>
        <Col sm={6}>
          <Label><b></b></Label>
        </Col>
      </Row>
      {!isEmpty(errorMessage) ?
        <div className="row">
          <div className="col-md-12" style={{ marginLeft: '5px' }}>
            <label style={{ color: "red", whiteSpace: "pre" }}>
              {errorMessage}
            </label>
          </div>
        </div>
        :
        <div />
      }
      <Row>
        <Col sm={6}>
          <Label><b>Nº do AI:</b></Label>
          <Input
            name="report_ai"
            value={treatNullValue(data[REPORT_AI])}
            onBlur={e => validateInput()}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_AI]) || data[REPORT_AI] === oldData[REPORT_AI])}
            style={{ height: '2.8em' }}
            placeholder={aiInput.placeholder}
            onChange={aiInput.onChange(data[REPORT_AI], (value) => setData({ ...data, [REPORT_AI]: value }))}
          />
        </Col>
        <Col sm={6}>
          <Label><b>Nº do DAR</b></Label>
          <Input
            name="report_dar"
            value={treatNullValue(data[REPORT_DAR])}
            onBlur={e => validateInput()}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_DAR]) || data[REPORT_DAR] === oldData[REPORT_DAR])}
            onKeyPress={event => { LetterFilter(event, 11) }}
            onChange={e => setData({ ...data, [REPORT_DAR]: e.target.value })}
            style={{ height: '2.8em' }}
          />
        </Col>
      </Row>

      <Row style={{ marginTop: '10px' }}>
        <Col sm={4}>
          <Label><b>Nº código da receita:</b></Label>
          <Input
            name="report_revenue_code"
            style={{ height: '2.8em' }}
            onBlur={e => validateInput()}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_REVENUE_CODE]) || data[REPORT_REVENUE_CODE] === oldData[REPORT_REVENUE_CODE])}
            onKeyPress={event => { LetterFilter(event, 4) }}
            value={treatNullValue(data[REPORT_REVENUE_CODE])}
            onChange={e => setData({ ...data, [REPORT_REVENUE_CODE]: e.target.value })}
          />
        </Col>

        <Col sm={8}>
          <Label><b>Valor autuado:</b></Label>
          <Input
            name="report_penalty_value"
            value={treatNullValue(data[REPORT_PENALTY_VALUE])}
            onBlur={e => validateInput()}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_PENALTY_VALUE]) || data[REPORT_PENALTY_VALUE] === oldData[REPORT_PENALTY_VALUE])}
            onKeyPress={event => { LetterFilter(event) }}
            onChange={e => setData({ ...data, [REPORT_PENALTY_VALUE]: e.target.value })}
            style={{ height: '2.8em' }}
          />
        </Col>
      </Row>

      <Row style={{ marginTop: '10px' }}>
        <Col sm={3}>
          <Label><b>Placa do veículo:</b></Label>
          <Input
            name="vehicle_plate"
            value={data[EVENT_VEHICLE_PLATE]}
            onKeyPress={event => { AccentFilter(event, 8) }}
            onChange={e => setData({ ...data, [EVENT_VEHICLE_PLATE]: e.target.value })}
            style={{ height: '2.8em', pointerEvents: 'none', cursor: 'not-allowed', backgroundColor: '#E3E3E3', color: '#66615B' }}
          />
        </Col>

        <Col sm={3}>
          <Label><b>Placa do reboque:</b></Label>
          <Input
            name="trailer_plate"
            onKeyPress={event => { AccentFilter(event, 8) }}
            onBlur={e => validateInput()}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[TRAILER_PLATE]) || data[TRAILER_PLATE] === oldData[TRAILER_PLATE])}
            value={treatNullValue(data[TRAILER_PLATE])}
            onChange={e => setData({ ...data, [TRAILER_PLATE]: e.target.value })}
            style={{ height: '2.8em' }}

          />
        </Col>
        <Col sm={6}>
          <Label><b>CPF do condutor:</b></Label>
          <Input
            name={REPORT_CPF_DRIVER}
            //onKeyPress={event => { LetterFilter(event, 11) }}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_CPF_DRIVER]) || data[REPORT_CPF_DRIVER] === oldData[REPORT_CPF_DRIVER])}
            value={data[REPORT_CPF_DRIVER] || ''}
            onBlur={e => validateInput()}

            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            style={{ height: '2.8em' }}
            placeholder={cpfInput.placeholder}
            onChange={cpfInput.onChange(data[REPORT_CPF_DRIVER], (value) => setData({ ...data, [REPORT_CPF_DRIVER]: value }))}
          />
        </Col>
      </Row>

      <Row style={{ marginTop: '10px' }}>
        <Col sm={4}>
          <Label><b>CPF do autuado:</b></Label>
          <Input
            name={REPORT_BOOKED_CPF}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_BOOKED_CPF]) || data[REPORT_BOOKED_CPF] === oldData[REPORT_BOOKED_CPF])}
            value={data[REPORT_BOOKED_CPF] || ''}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            onBlur={e => validateInput()}
            //onKeyPress={event => { LetterFilter(event, 11) }}
            style={{ height: '2.8em' }}
            placeholder={cpfInput.placeholder}
            onChange={cpfInput.onChange(data[REPORT_BOOKED_CPF], (value) => setData({ ...data, [REPORT_BOOKED_CPF]: value }))}
          />
        </Col>

        <Col sm={4}>
          <Label><b>CNPJ do autuado:</b></Label>
          <Input
            name={REPORT_BOOKED_CNPJ}
            value={data[REPORT_BOOKED_CNPJ] || ''}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            onBlur={e => validateInput()}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_BOOKED_CNPJ]) || data[REPORT_BOOKED_CNPJ] === oldData[REPORT_BOOKED_CNPJ])}
            //onKeyPress={event => { LetterFilter(event, 14) }}
            style={{ height: '2.8em' }}
            placeholder={cnpjInput.placeholder}
            onChange={cnpjInput.onChange(data[REPORT_BOOKED_CNPJ], (value) => setData({ ...data, [REPORT_BOOKED_CNPJ]: value }))}
          />
        </Col>

        <Col sm={4}>
          <Label><b>IE do autuado:</b></Label>
          <Input
            name={REPORT_BOOKED_IE}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_BOOKED_IE]) || data[REPORT_BOOKED_IE] === oldData[REPORT_BOOKED_IE])}
            style={{ height: '2.8em' }}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            onBlur={e => validateInput()}
            onKeyPress={event => { LetterFilter(event, 20) }}
            value={treatNullValue(data[REPORT_BOOKED_IE])}
            onChange={e => setData({ ...data, [REPORT_BOOKED_IE]: e.target.value })}
          />
        </Col>
      </Row>

      <Row style={{ marginTop: '10px' }}>
        <Col sm={6}>
          <Label><b>Latitude do local da atuação:</b></Label>
          <Input
            onKeyPress={event => { NegativeValuesFilter(event, 20) }}
            value={treatNullValue(data[REPORT_LATITUDE])}
            onBlur={e => validateInput()}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_LATITUDE]) || data[REPORT_LATITUDE] === oldData[REPORT_LATITUDE])}
            onChange={e => setData({ ...data, [REPORT_LATITUDE]: e.target.value })}
            name={REPORT_LATITUDE}
            style={{ height: '2.8em' }}

          />
        </Col>
        <Col sm={6}>
          <Label><b>Longitude do local da atuação:</b></Label>
          <Input
            value={treatNullValue(data[REPORT_LONGITUDE])}
            onBlur={e => validateInput()}
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_LONGITUDE]) || data[REPORT_LONGITUDE] === oldData[REPORT_LONGITUDE])}
            onChange={e => setData({ ...data, [REPORT_LONGITUDE]: e.target.value })}
            onKeyPress={event => { NegativeValuesFilter(event, 20) }}
            name={REPORT_LONGITUDE}
            style={{ height: '2.8em' }}
          />
        </Col>
      </Row>
      <Row style={{ marginTop: '10px' }}>
        <Col sm={12}>
          <Label><b>Produto:</b></Label>
          <Input
            type="textArea"
            readOnly={isDetailedReportDisabled(readOnly)}
            disabled={isDetailedReportDisabled(readOnly)}
            invalid={invalid && data[REPORT_ENABLED] && (isEmpty(data[REPORT_PRODUCT_NAME]) || data[REPORT_PRODUCT_NAME] === oldData[REPORT_PRODUCT_NAME])}
            value={treatNullValue(data[REPORT_PRODUCT_NAME])}
            onBlur={e => validateInput()}
            onChange={e => setData({ ...data, [REPORT_PRODUCT_NAME]: e.target.value })}
            name={REPORT_PRODUCT_NAME}
            style={{ height: '5.8em' }}
          />
        </Col>
      </Row>
    </>)
}
