import { NumericFilter } from "common/utils/filters";
import { LetterFilter } from "common/utils/filters";
import { AuditorsContext } from "context/AuditorsContext";
import React from "react";
import { FcCheckmark, FcSearch } from "react-icons/fc";
import {
    Input,
    Button,
    Modal,
    ModalBody,
    ModalHeader,
    Col,
    Row,
    Table
} from 'reactstrap';



export const BLANK_AUDITOR_MODAL_DATA = {
    "code": "",
    "name": ""    
  }

export default function AuditorFindModal({ show, toggle, auditorData, setAuditorData, setCodeAuditorResponsible,  setAuditorReponsible}) {

    const [auditorsDataModal, setAuditorsDataModal] = React.useState([])
    const {auditorsData } = React.useContext(AuditorsContext);

    const hasSubstring = (data, key, substring) => {
        if (data[key].toUpperCase().indexOf(substring.toUpperCase()) !== -1) {
          return true;
        } else {
          return false;
        }
    
      }

    const handleFicalFindButton = () => {
        let result = []
    
        for (let i = 0; i < auditorsData.length; i++) {
          if ((auditorData.code && hasSubstring(auditorsData[i], "user_registration_id", auditorData.code)) || (auditorData.name && hasSubstring(auditorsData[i], "user_name", auditorData.name))) {
            result.push({ auditorID: auditorsData[i]["user_registration_id"], auditorName: auditorsData[i]["user_name"] });
          }
        }
    
        setAuditorsDataModal(() => { return result })
      }

    return (
    
        <Modal isOpen={show} toggle={toggle} size="lg" >
            <ModalHeader toggle={toggle} style={{ padding: "10px 15px" }}>
                Buscar Auditor
            </ModalHeader>
            <ModalBody style={{ alignContent: 'center', paddingTop: "4px" }}>

                <Row style={{ marginTop: '5px' }}>
                    <Col>
                        <b> Filtros</b>
                        <div className="input-group mb-3" style={{ marginTop: '5px' }}>

                            <div className="form-row">

                                <div className="form-group col-md-3">
                                    <label htmlFor="inputMat">Matrícula</label>
                                    <Input className="form-control" id="inputMat" value={auditorData.code} maxLength="7"
                                        onChange={event => { setAuditorData({...auditorData, code:event.target.value})}}
                                        onKeyUp={event => event.key === "Enter" && handleFicalFindButton()}
                                        onKeyPress={event => { LetterFilter(event, 8) }}
                                    />
                                </div>
                                <div className="form-group col-md-7">
                                    <label htmlFor="inputNome">Nome</label>
                                    <Input
                                        className="form-control" id="inputNome" value={auditorData.name}
                                        onChange={event => { setAuditorData({...auditorData, name:event.target.value})}}
                                        onKeyUp={event => event.key === "Enter" && handleFicalFindButton()}
                                        onKeyPress={event => { NumericFilter(event) }}
                                    />
                                </div>
                                <div class="form-group col-md-2">
                                    <label htmlFor="btnFindAuditor" style={{ color: "white" }}>Buscar</label>
                                    <Button size="sm" color="primary" outline id="btnFindAuditor"
                                        disabled={auditorData.code === '' && auditorData.name === ''}
                                        class="form-control" onClick={handleFicalFindButton} >
                                        <FcSearch size={'1.5rem'} />
                                    </Button>
                                </div>
                            </div>
                        </div>
                    </Col>
                </Row>
                {Array.isArray(auditorsDataModal) && auditorsDataModal.length > 0 ?
                    <Row style={{ margin: '0 0 20px 0' }}>
                        <Table responsive hover>
                            <thead className="text-primary">
                                <tr>
                                    <th>Matrícula</th>
                                    <th>Nome</th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    auditorsDataModal.map((auditor, index) => {
                                        return (
                                            <tr key={index} onDoubleClick={() => { setAuditorData({code:auditor["auditorID"], name:auditor["auditorName"]}); toggle() }}>
                                                <td style={{ wordBreak: 'break-word' }}>{auditor["auditorID"]}</td>
                                                <td style={{ wordBreak: 'break-word' }}>{auditor["auditorName"]}</td>
                                                <td style={{ wordBreak: 'break-word' }}><Button color="primary"
                                                    onClick={() => { setAuditorData({code:auditor["auditorID"], name:auditor["auditorName"]}); toggle() }}>
                                                    Selecionar <FcCheckmark size={'1.5rem'} /></Button></td>
                                            </tr>
                                        )
                                    })
                                }
                            </tbody>
                        </Table>
                    </Row>
                    : <div></div>
                }
            </ModalBody>
        </Modal>

    );


}
