import { calculateComponentHeight } from "common/utils/forms";
import React from 'react';
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Modal,
  ModalBody,
  ModalHeader,
  ModalFooter,
  Col,
  Spinner,
} from 'reactstrap';

export const INPUT = 1
export const LABEL = 2
export const DISCLAIMER = 3


export function createRevisionModalData(title, labels, values, types = [], allowDuplication) {

  let data = {
    title: title,
    values: [],
    buttonLabel: "Salvar",
    displayButton: true,
  }

  if (typeof allowDuplication !== 'undefined') {
    data['buttonLabel'] = (allowDuplication) ? "Salvar mesmo assim" : ""
    data['displayButton'] = allowDuplication
  }

  for (let i = 0; i < labels.length; i++) {
    data.values.push({ label: labels[i], value: values[i], type: types[i] })
  }
  return data
}

export default function RevisionModal({ show, toggle, data, spinner, actionMethod, buttonText }) {

  return <Modal isOpen={show} toggle={toggle} size="lg">
    <ModalHeader toggle={toggle} style={{ padding: "10px 15px" }}>{data.title}</ModalHeader>
    <ModalBody style={{ paddingTop: "4px" }}>
      <Form>
        {(data.values && Array.isArray(data.values)) ? data.values.map((item, index) => (
          <FormGroup row key={index}>
            <Label for="exampleEmail" sm={6}><b>{item.label}</b></Label>
            <Col sm={12}>
              {(item.type && item.type === INPUT) ?
                <Input type='textarea' readOnly value={item.value}
                  maxLength="2048" style={calculateComponentHeight(item.value)}
                /> : (item.type && item.type === DISCLAIMER) ?
                <Label style={{ wordBreak: 'break-word', color:'red' }}>
                  {item.value}
                </Label>
                : <Label style={{whiteSpace: 'pre', wordBreak: 'break-word' }}>
                {item.value}
              </Label>
              }
            </Col>
          </FormGroup>
        )) : " "}

      </Form>
    </ModalBody>
    <ModalFooter style={{ justifyContent: 'center' }}>
      {data.displayButton && <Button color={spinner ? "success" : "primary"} onClick={actionMethod}>{data.buttonLabel} {spinner && <Spinner size="sm" />}</Button>}
      <Button color="secondary" onClick={toggle}>Cancelar</Button>{' '}
    </ModalFooter>
  </Modal>;
}
