import { Col, Label, Row } from "reactstrap"
import { RiNotification2Line } from "react-icons/ri"

import CTE from "common/model/Cte"
import { getCteATFLink } from "common/enviroment"
import { cpfOrCnpjThatsTheQuestion } from "common/utils/validate";
import { convertUtcToBrazilianDate } from "common/utils/validate"
import Table from "components/tables/Table"

export default function CteTable({ data, headerText, cteOfInterest, loading, showBell = false }) {
  return <div className="content">
    <Row>
      <Col md="12">
        <Label style={{ marginRight: '5px' }}><b>{headerText}</b></Label>
      </Col>
    </Row>
    <Row>
      <Col md="12">
        <Table
          data={data}
          loading={[loading, () => { }]}
          hidePagination
          columns={{
            " ": {
              args: CTE.KEY,
              Func: ({ data }) => (showBell && cteOfInterest === data) && <RiNotification2Line size={15} />
            },
            "#": {
              Func: ({ index }) => <>{index}</>
            },
            "Data Emissão": {
              args: CTE.DHEMI,
              func: date => convertUtcToBrazilianDate(date)
            },
            "Emitente": {
              args: [CTE.EMIT_CPF, CTE.EMIT_CNPJ],
              func: ([cpf, cnpj]) => cpfOrCnpjThatsTheQuestion(cpf, cnpj)
            },
            "Destinatário": {
              args: [CTE.DEST_CPF, CTE.DEST_CNPJ],
              func: ([cpf, cnpj]) => cpfOrCnpjThatsTheQuestion(cpf, cnpj)
            },
            "UF Origem": CTE.ORIGIN,
            "UF Destino": CTE.DESTINATION,
            "Valor": {
              args: CTE.TOTAL_VALUE,
              func: value => `R$${value.toLocaleString('pt-br', { minimumFractionDigits: 2 })}`
            },
            "Situação": CTE.STATUS,
            "Chave da CTe": {
              args: CTE.KEY,
              Func: ({ data }) => (
                <a
                  style={{ color: '#2322c8' }}
                  href={getCteATFLink(data)}
                  target="_blank"
                  rel="noreferrer"
                >
                  {data}
                </a>
              )
            }
          }}
        />
      </Col>
    </Row>
  </div>
}
