import React from "react";
import {
  Pagination,
  PaginationItem,
  PaginationLink
} from "reactstrap";
import { RiArrowRightLine, RiArrowRightSLine, RiArrowLeftSLine, RiArrowLeftLine } from "react-icons/ri";
import { v4 } from 'uuid'


export default function NewPagination({ state, isLoading, maxIndex }) {
  const [index, setIndex] = state
  const [pages, setPages] = React.useState([...Array(maxIndex >= 0 ? Math.min(maxIndex + 1, 5) : 5).keys()])

  const nextPage = () => {
    setIndex(index + 1)
  }

  const previousPage = () => {
    setIndex(index - 1)
  }

  const len = (number) => Math.log(number) * Math.LOG10E + 1 | 0;

  React.useEffect(() => {
    if (pages.length !== 5)
      return

    if (index >= 2 && (maxIndex <= 0 || index <= maxIndex - 2))
      setPages(Array.from({ length: 5 }, (_, k) => k + index - 2))
    else if (index < 4)
      setPages(Array.from({ length: 5 }, (_, k) => k))
    else
      setPages(Array.from({ length: 5 }, (_, k) => maxIndex + k - 4))
    // eslint-disable-next-line react-hooks/exhaustive-deps  
  }, [index, maxIndex])

  return (
    <>
      <Pagination style={{ justifyContent: "center", marginTop: "1rem", height: "40px" }}>
        <PaginationItem disabled={index === 0}>
          <PaginationLink
            first
            href="#"
            key={v4()}
            onClick={() => { setIndex(0) }}
            style={{ color: index > 0 ? "black" : "#a2a2a2", border: 0, fontSize: 22 }}
          >
            <RiArrowLeftLine />
          </PaginationLink>
        </PaginationItem>
        <PaginationItem disabled={(index <= 0 || isLoading)}>
          <PaginationLink
            href="#"
            previous
            key={v4()}
            onClick={previousPage}
            style={{ color: index > 0 ? "black" : "#a2a2a2", fontSize: 22, border: 0 }}
          >
            <RiArrowLeftSLine />
          </PaginationLink>
        </PaginationItem>

        {pages.map(page => {
          return (
            <PaginationItem disabled={maxIndex >= 0 && page > maxIndex} key={v4()} active={index === page}>
              <PaginationLink
                href={index === page ? null : "#"}
                onClick={() => { index !== page && setIndex(page) }}
                key={v4()}
                style={{
                  backgroundColor: index === page ? "#51cbce" : "white",
                  borderColor: index === page ? "#51cbce" : "#dee2e6",
                  color: index === page ? "white" : (maxIndex >= 0 && page > maxIndex) ? "#a2a2a2" : "#51cbce",
                  cursor: index === page ? "default" : "pointer",
                  padding: index === page ? `0.75rem ${len(index) * 0.25}rem` : "0.75rem 1rem",
                  borderRadius: 4,
                  border: "0",
                  fontSize: 18,
                  fontWeight: "normal"
                }}
              >
                {index === page ?
                  <input
                    placeholder={index + 1}
                    style={{ maxWidth: `${2 + 0.27 * len(index)}rem`, border: 0, backgroundColor: "rgb(81, 203, 206)", textAlign: "center", color: "white" }}
                    onKeyUp={e => {
                      if (e.key === "Enter" || e.keyCode === 13) {
                        const value = +e.target.value - 1
                        if (value < 0) {
                          setIndex(0)
                          return
                        }
                        if (maxIndex > 0 && value > maxIndex) {
                          setIndex(maxIndex)
                          return
                        }
                        setIndex(value)
                      }
                    }}
                  />
                  :
                  page + 1
                }
              </PaginationLink>
            </PaginationItem>
          )
        })}

        <PaginationItem disabled={maxIndex >= 0 ? index >= maxIndex : isLoading}>
          <PaginationLink
            href="#"
            next
            key={v4()}
            onClick={nextPage}
            style={{ color: (maxIndex >= 0 && index >= maxIndex) ? "#a2a2a2" : "black", border: 0, fontSize: 22 }}
          >
            <RiArrowRightSLine />
          </PaginationLink>
        </PaginationItem>
        <PaginationItem disabled={(maxIndex >= 0 && index >= maxIndex) || isLoading || maxIndex < 0}>
          <PaginationLink
            href="#"
            last
            key={v4()}
            onClick={() => { setIndex(maxIndex) }}
            style={{ color: (maxIndex >= 0 && index >= maxIndex) || maxIndex < 0 ? "#a2a2a2" : "black", border: 0, fontSize: 22 }}
          >
            <RiArrowRightLine />
          </PaginationLink>
        </PaginationItem>
      </Pagination>
    </>
  );
}
